import React, { useState } from "react";
import { useParams } from "react-router-dom";
import CalendarComponent from "./Dates/components/CalendarComponent";
import TimeSlotModal from "./Dates/components/TimeSlotModal";
import MealSelectionModal from "./Dates/components/MealSelectionModal";
import NavbarBooking from "./Dates/components/NavbarBooking";
import useFetchDatesEffect from "../hooks/useFetchDatesEffect";
import {handleSelectSlot} from "./Dates/handlers/BookingHandlers";
import { handleMealSelection, handleBooking, handleTimeSelection } from "./Dates/handlers/BookingHandlers";
import { closeForm } from './';
import "../styles/Dates.css";

const Dates = () => {
  const { userId } = useParams();
  const [category, setCategory] = useState("");
  const [events, setEvents] = useState([]);
  const [selectedDate, setSelectedDate] = useState(null);
  const [selectedTimeSlot, setSelectedTimeSlot] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [showMealsSelection, setShowMealsSelection] = useState(false);
  const [bookingStatus, setBookingStatus] = useState([]);
  const [userType, setUserType] = useState(null);
  const [numOfPeople, setNumOfPeople] = useState("");
  const [hallCapacity, setHallCapacity] = useState(null);
  const [minGuests, setMinGuests] = useState(null);
  const [exclusiveMeal, setExclusiveMeal] = useState(null);
  const [regularMeals, setRegularMeals] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);
  const [pricePerEvent, setPricePerEvent] = useState(0);
  const [advancePayment, setAdvancePayment] = useState(0);
  const [selectedMeal, setSelectedMeal] = useState(null);

  useFetchDatesEffect(userId, setCategory, setEvents, setUserType, setHallCapacity, setMinGuests, setPricePerEvent, setRegularMeals, setExclusiveMeal);

  return (
    <div className="dates-container">
      <NavbarBooking userId={userId} />
      <h2>Booked Dates</h2>
      <CalendarComponent
        events={events}
        onSelectSlot={(slotInfo) =>
          handleSelectSlot({
            start: slotInfo.start,
            userType,
            setSelectedDate,
            setBookingStatus,
            setShowModal,
            userId,
          })
        }
      />
      <TimeSlotModal
        showModal={showModal}
        category={category}
        selectedDate={selectedDate}
        dayBooking={bookingStatus.some((booking) => booking.time_slot === "day")}
        nightBooking={bookingStatus.some((booking) => booking.time_slot === "night")}
        numOfPeople={numOfPeople}
        setNumOfPeople={setNumOfPeople}
        minGuests={minGuests}
        hallCapacity={hallCapacity}
        pricePerEvent={pricePerEvent}
        handleTimeSelection={(time) =>
          handleTimeSelection({
            time,
            category,
            numOfPeople,
            minGuests,
            hallCapacity,
            setShowModal,
            setShowMealsSelection,
            setTotalPrice,
            pricePerEvent,
            handleBooking,
          })
        }
        closeModal={() => setShowModal(false)}
      />
      <MealSelectionModal
        showMealsSelection={showMealsSelection}
        regularMeals={regularMeals}
        exclusiveMeal={exclusiveMeal}
        selectedMeal={selectedMeal}
        handleMealSelection={(meal) =>
          handleMealSelection({
            meal,
            selectedMeal,
            setSelectedMeal,
            setTotalPrice,
            pricePerEvent,
            setAdvancePayment,
          })
        }
        totalPrice={totalPrice}
        advancePayment={advancePayment}
        handleBooking={(timeSlot) =>
          handleBooking({
            timeSlot,
            selectedMeal,
            category,
            numOfPeople,
            userId,
            selectedDate,
            totalPrice,
            advancePayment,
            closeForm,
          })
        }
        closeForm={() =>
          closeForm({
            setSelectedMeal,
            setTotalPrice,
            setSelectedDate,
            setSelectedTimeSlot,
            setNumOfPeople,
            setShowModal,
            setShowMealsSelection,
            pricePerEvent,
          })
        }
      />
    </div>
  );
};

export default Dates;
