import React, { useState, useEffect } from 'react';
import '../styles/Services.css';

const Services = () => {
  const [exclusiveMeal, setExclusiveMeal] = useState(null);
  const [regularMeals, setRegularMeals] = useState([]);
  const [newMeal, setNewMeal] = useState({ name: '', description: '', image: null, price: '' });
  const [showExclusiveForm, setShowExclusiveForm] = useState(false);
  const [showRegularForm, setShowRegularForm] = useState(false);
  const [editMode, setEditMode] = useState({ isEditing: false, mealType: null, mealId: null });

  useEffect(() => {
    fetch('/api/meals/regular')
      .then(response => response.json())
      .then(data => setRegularMeals(data))
      .catch(error => console.error('Error fetching regular meals:', error));

    fetch('/api/meals/exclusive')
      .then(response => response.json())
      .then(data => setExclusiveMeal(data[0]))
      .catch(error => console.error('Error fetching exclusive meal:', error));
  }, []);

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    setNewMeal((prevMeal) => ({
      ...prevMeal,
      image: file
    }));
  };

  const startEditMeal = (mealType, meal) => {
    setNewMeal(meal);
    setEditMode({ isEditing: true, mealType, mealId: meal.id });
    if (mealType === 'exclusive') {
      setShowExclusiveForm(true);
    } else {
      setShowRegularForm(true);
    }
  };

  const saveUpdatedMeal = () => {
    const formData = new FormData();
    formData.append('name', newMeal.name);
    formData.append('description', newMeal.description);
    formData.append('price', newMeal.price);
    if (newMeal.image instanceof File) {
      formData.append('image', newMeal.image);
    }

    const apiPath = editMode.mealType === 'exclusive' ? `/api/meals/exclusive/${editMode.mealId}` : `/api/meals/regular/${editMode.mealId}`;

    fetch(apiPath, {
      method: 'PUT',
      body: formData,
    })
    .then(response => response.json())
    .then(data => {
      console.log(`${editMode.mealType} meal updated:`, data);
      // Refresh state after updating
      if (editMode.mealType === 'exclusive') {
        setExclusiveMeal(newMeal);
      } else {
        setRegularMeals(regularMeals.map(meal => (meal.id === editMode.mealId ? newMeal : meal)));
      }
      cancelEdit(); // Reset form and edit mode
    })
    .catch(error => console.error(`Error updating ${editMode.mealType} meal:`, error));
  };

  const cancelEdit = () => {
    setNewMeal({ name: '', description: '', image: null, price: '' });
    setEditMode({ isEditing: false, mealType: null, mealId: null });
    setShowExclusiveForm(false);
    setShowRegularForm(false);
  };

  return (
    <div className="profile-container">
      <h1>Hall Services</h1>

      <h2>Exclusive Meal</h2>
      {!showExclusiveForm && !editMode.isEditing && (
        <button onClick={() => setShowExclusiveForm(true)}>Add Exclusive Meal</button>
      )}
      {(showExclusiveForm || (editMode.isEditing && editMode.mealType === 'exclusive')) && (
        <div>
          <input
            type="text"
            placeholder="Meal Name"
            value={newMeal.name || ''}
            onChange={(e) => setNewMeal({ ...newMeal, name: e.target.value })}
          />
          <input
            type="text"
            placeholder="Description"
            value={newMeal.description || ''}
            onChange={(e) => setNewMeal({ ...newMeal, description: e.target.value })}
          />
          <input
            type="number"
            placeholder="Price"
            value={newMeal.price || ''}
            onChange={(e) => setNewMeal({ ...newMeal, price: e.target.value })}
          />
          <input
            type="file"
            onChange={handleImageUpload}
          />
          {newMeal.image && !(newMeal.image instanceof File) && (
            <img src={`/uploads/${newMeal.image}`} alt={newMeal.name} style={{ width: '100px', height: '100px' }} />
          )}
          {editMode.isEditing ? (
            <div>
              <button onClick={saveUpdatedMeal}>Save Changes</button>
              <button onClick={cancelEdit}>Cancel</button>
            </div>
          ) : (
            <div>
              <button onClick={addExclusiveMeal}>Save Exclusive Meal</button>
              <button onClick={() => setShowExclusiveForm(false)}>Cancel</button>
            </div>
          )}
        </div>
      )}

      {exclusiveMeal && !editMode.isEditing && (
        <div className="meal-container">
          <img src={`/uploads/${exclusiveMeal.image}`} alt={exclusiveMeal.name} style={{ width: '100px', height: '100px' }} />
          <h3>{exclusiveMeal.name}</h3>
          <p>{exclusiveMeal.description}</p>
          <p>{exclusiveMeal.price}</p>
          <div className="dropdown">
            <button className="dropbtn">⋮</button>
            <div className="dropdown-content">
              <button onClick={() => startEditMeal('exclusive', exclusiveMeal)}>Update</button>
              <button onClick={deleteExclusiveMeal}>Remove</button>
            </div>
          </div>
        </div>
      )}

      <h2>Regular Meals</h2>
      {!showRegularForm && !editMode.isEditing && (
        <button onClick={() => setShowRegularForm(true)}>Add Regular Meal</button>
      )}
      {(showRegularForm || (editMode.isEditing && editMode.mealType === 'regular')) && (
        <div>
          <input
            type="text"
            placeholder="Meal Name"
            value={newMeal.name || ''}
            onChange={(e) => setNewMeal({ ...newMeal, name: e.target.value })}
          />
          <input
            type="text"
            placeholder="Description"
            value={newMeal.description || ''}
            onChange={(e) => setNewMeal({ ...newMeal, description: e.target.value })}
          />
          <input
            type="number"
            placeholder="Price"
            value={newMeal.price || ''}
            onChange={(e) => setNewMeal({ ...newMeal, price: e.target.value })}
          />
          <input
            type="file"
            onChange={handleImageUpload}
          />
          {newMeal.image && !(newMeal.image instanceof File) && (
            <img src={`/uploads/${newMeal.image}`} alt={newMeal.name} style={{ width: '100px', height: '100px' }} />
          )}
          {editMode.isEditing ? (
            <div>
              <button onClick={saveUpdatedMeal}>Save Changes</button>
              <button onClick={cancelEdit}>Cancel</button>
            </div>
          ) : (
            <div>
              <button onClick={addRegularMeal}>Save Regular Meal</button>
              <button onClick={() => setShowRegularForm(false)}>Cancel</button>
            </div>
          )}
        </div>
      )}

      <ul>
        {regularMeals.map((meal, index) => (
          <li key={index} className="meal-container">
            <img src={`/uploads/${meal.image}`} alt={meal.name} style={{ width: '100px', height: '100px' }} />
            <h3>{meal.name}</h3>
            <p>{meal.description}</p>
            <p>{meal.price}</p>
            <div className="dropdown">
              <button className="dropbtn">⋮</button>
              <div className="dropdown-content">
                <button onClick={() => startEditMeal('regular', meal)}>Update</button>
                <button onClick={() => deleteRegularMeal(meal.id)}>Remove</button>
              </div>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Services;
