// src/components/BookingCalendar.js
import { API_URL } from '../constans';
import React, { useState, useEffect } from 'react';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';

const localizer = momentLocalizer(moment);

const BookingCalendar = () => {
  const [events, setEvents] = useState([]);
  const [selectedDate, setSelectedDate] = useState(null);
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    idNum: '',
    phoneNumber: '',
  });

  useEffect(() => {
    fetch(`${API_URL}/api/events`)
      .then((response) => response.json())
      .then((data) => setEvents(data))
      .catch((error) => console.error('Error fetching events:', error));
  }, []);

  const handleSelectSlot = ({ start, end }) => {
    setSelectedDate(start);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({ ...prevData, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const numOfBusiness = localStorage.getItem('numOfBusiness'); // Assuming you store this in localStorage

    fetch(`${API_URL}/api/events`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ title: `${formData.firstName} ${formData.lastName}`, start: selectedDate, end: selectedDate, description: formData.idNum }),
    })
      .then((response) => response.json())
      .then((data) => {
        alert(data.message);
        setFormData({ firstName: '', lastName: '', idNum: '', phoneNumber: '' });
        setSelectedDate(null);
        setEvents((prevEvents) => [...prevEvents, { start: selectedDate, end: selectedDate, title: `${formData.firstName} ${formData.lastName}` }]);
      })
      .catch((error) => console.error('Error storing date selection:', error));
  };

  return (
    <div className="booking-calendar-container">
      <Calendar
        localizer={localizer}
        events={events}
        selectable
        onSelectSlot={handleSelectSlot}
        defaultView="month"
        style={{ height: 500 }}
      />
      {selectedDate && (
        <form className="date-form" onSubmit={handleSubmit}>
          <h3>Selected Date: {moment(selectedDate).format('MMMM Do YYYY')}</h3>
          <div className="form-group">
            <label>First Name</label>
            <input
              type="text"
              name="firstName"
              value={formData.firstName}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Last Name</label>
            <input
              type="text"
              name="lastName"
              value={formData.lastName}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>ID Number</label>
            <input
              type="text"
              name="idNum"
              value={formData.idNum}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Phone Number</label>
            <input
              type="text"
              name="phoneNumber"
              value={formData.phoneNumber}
              onChange={handleInputChange}
              required
            />
          </div>
          <button type="submit">Submit</button>
        </form>
      )}
    </div>
  );
};

export default BookingCalendar;
