import React, { useState } from 'react';
import { API_URL } from '../constans';
const BusinessProfileDetailsDisplay = ({ userDetails, setEditing }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [hallCapacity, setHallCapacity] = useState(userDetails.hallCapacity || '');

  const handleUpdate = async () => {
    try {
      const response = await fetch(`${API_URL}/api/business/${userDetails.id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          businessName: userDetails.businessName,
          numOfBusiness: userDetails.numOfBusiness,
          address: userDetails.address,
          email: userDetails.email,
          hallCapacity: hallCapacity, // Updated hallCapacity
        }),
      });

      const data = await response.json();
      if (response.ok) {
        alert(data.message);
        setIsEditing(false); // Exit editing mode
      } else {
        alert(data.message || "Error updating user profile");
      }
    } catch (error) {
      console.error("Error updating user profile:", error);
      alert("Error updating user profile. Please try again.");
    }
  };

  return (
    <div>
      <p><strong>Business Name:</strong> {userDetails.businessName}</p>
      <p><strong>Address:</strong> {userDetails.address}</p>
      <p><strong>Email:</strong> {userDetails.email}</p>
      <p><strong>Category:</strong> {userDetails.category}</p>
      {isEditing ? (
        <div>
          <label>Hall Capacity:</label>
          <input
            type="number"
            value={hallCapacity}
            onChange={(e) => setHallCapacity(e.target.value)}
            min="0" // Prevent negative input
          />
          <button onClick={handleUpdate}>Save</button>
          <button onClick={() => setIsEditing(false)}>Cancel</button>
        </div>
      ) : (
        <div>
          <p><strong>Hall Capacity:</strong> {userDetails.hallCapacity}</p>
          <button onClick={() => setIsEditing(true)}>Edit</button>
        </div>
      )}
    </div>
  );
};

export default BusinessProfileDetailsDisplay;
