import moment from "moment";
import React, { useEffect, useState } from "react";
import { Calendar, momentLocalizer } from "react-big-calendar";
import "react-big-calendar/lib/css/react-big-calendar.css";
import { useParams } from "react-router-dom";

import "../styles/Dates.css";
import NavbarBooking from "./NavbarBooking";

const localizer = momentLocalizer(moment);

// Modal Component for displaying meal selection
const Modal = ({ isOpen, onClose, children }) => {
  if (!isOpen) return null;

  return (
    <div className='modal-overlay'>
      <div className='modal-content'>
        <button onClick={onClose} className='modal-close'>
          Close
        </button>
        {children}
      </div>
    </div>
  );
};

const Dates = () => {
  const { userId } = useParams();
  const [category, setCategory] = useState(""); // State to hold the category fetched from the server
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedDate, setSelectedDate] = useState(null);
  const [selectedTimeSlot, setSelectedTimeSlot] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [showMealsSelection, setShowMealsSelection] = useState(false);
  const [bookingStatus, setBookingStatus] = useState([]);
  const [userType, setUserType] = useState(null);
  const [numOfPeople, setNumOfPeople] = useState("");
  const [hallCapacity, setHallCapacity] = useState(null);
  const [minGuests, setMinGuests] = useState(null);
  const [exclusiveMeal, setExclusiveMeal] = useState(null);
  const [regularMeals, setRegularMeals] = useState([]);
  const [selectedMeals, setSelectedMeals] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);
  const [pricePerEvent, setPricePerEvent] = useState(0);

  useEffect(() => {
    const fetchUserType = () => {
      const storedUserType = localStorage.getItem("userType");
      setUserType(storedUserType);
      console.log("User type fetched:", storedUserType);
    };

    const fetchCategory = async () => {
      try {
        console.log("Fetching category for userId:", userId);
        const response = await fetch(
          `/api/profile/business/${userId}/category`
        );
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const data = await response.json();
        console.log("Category fetched:", data.category);
        setCategory(data.category || "");
      } catch (error) {
        console.error("Error fetching category:", error.message);
        alert("Error fetching category. Please try again later.");
      }
    };

    const fetchHallDetails = async () => {
      try {
        console.log("Fetching hall details for userId:", userId);
        const response = await fetch(
          `/api/bookings/business/${userId}/capacity`
        );
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const data = await response.json();
        console.log("Hall details fetched:", data);
        setHallCapacity(data.hallCapacity || 0);
        setMinGuests(data.minGuests || 0);
        setPricePerEvent(data.price_per_event || 0); // Set price per event
      } catch (error) {
        console.error("Error fetching hall details:", error.message);
        alert("Error fetching hall details. Please try again later.");
      }
    };

    fetchUserType();
    fetchCategory();
    fetchHallDetails();

    const fetchBookedDates = async () => {
      try {
        console.log("Fetching booked dates for userId:", userId);
        const response = await fetch(
          `/api/profile/business/bookings/${userId}/booked-dates`
        );
        if (!response.ok) {
          throw new Error("Failed to fetch booked dates");
        }
        const data = await response.json();
        console.log("Booked dates fetched:", data.bookedDates);
        const formattedEvents = data.bookedDates.map((event) => ({
          start: new Date(event.booking_date),
          end: new Date(event.booking_date),
          title: `Booked: ${event.time_slot}`,
          className:
            event.time_slot === "day" ? "rbc-event-day" : "rbc-event-night",
        }));
        setEvents(formattedEvents);
      } catch (error) {
        console.error("Error fetching booked dates:", error.message);
        alert("Error fetching booked dates. Please try again later.");
      } finally {
        setLoading(false);
      }
    };

    fetchBookedDates();
  }, [userId]);

  useEffect(() => {
    if (userId) {
      console.log("Fetching meals for userId:", userId);
      fetch(`/api/meals/regular/user/${userId}`)
        .then((response) => response.json())
        .then((data) => {
          console.log("Regular meals fetched:", data);
          setRegularMeals(data);
        })
        .catch((error) => {
          console.error("Error fetching regular meals:", error.message);
          alert("Error fetching regular meals. Please try again later.");
        });

      fetch(`/api/meals/exclusive/user/${userId}`)
        .then((response) => response.json())
        .then((data) => {
          console.log("Exclusive meal fetched:", data[0]);
          setExclusiveMeal(data[0]);
        })
        .catch((error) => {
          console.error("Error fetching exclusive meal:", error.message);
          alert("Error fetching exclusive meal. Please try again later.");
        });
    }
  }, [userId]);

  const handleSelectSlot = ({ start }) => {
    const now = new Date();
    if (userType === "business") {
      alert("Business users cannot book dates.");
      return;
    }
    if (userType === null) {
      alert("You have to sign in first to book a date!");
      return;
    }
    if (start <= now) {
      alert("Please select a future date.");
      return;
    }
    setSelectedDate(start);
    const selectedDateString = start.toISOString().split("T")[0];
    console.log("Selected date:", selectedDateString);

    fetch(
      `/api/profile/business/bookings/check/${userId}/${selectedDateString}`
    )
      .then((response) => response.json())
      .then((data) => {
        if (!Array.isArray(data.bookedSlots)) {
          console.error("Invalid response:", data);
          return;
        }
        setBookingStatus(data.bookedSlots);
        console.log("Booking status fetched:", data.bookedSlots);
        const dayBooking = data.bookedSlots.some(
          (booking) => booking.time_slot === "day"
        );
        const nightBooking = data.bookedSlots.some(
          (booking) => booking.time_slot === "night"
        );

        if (dayBooking && nightBooking) {
          alert("This date is fully booked.");
        } else {
          setShowModal(true);
        }
      })
      .catch((error) => {
        console.error("Error checking booking status:", error.message);
        alert("Error checking booking status. Please try again later.");
      });
  };

  const handleMealSelection = (meal) => {
    setSelectedMeals([meal]); // Allow selecting only one meal at a time
    console.log("Meal selected:", meal);
  };

  const calculateTotalPrice = () => {
    const mealPrice = selectedMeals.length > 0 ? selectedMeals[0].price : 0;
    const total = mealPrice * numOfPeople + pricePerEvent;
    setTotalPrice(total);
    console.log("Total price calculated:", total);
  };

  const handleTimeSelection = (time) => {
    setSelectedTimeSlot(time);
    console.log("Time slot selected:", time);
    if (category === "Hall") {
      setShowModal(false);
      setShowMealsSelection(true);
    } else {
      // Instead of calling handleBooking immediately, we'll ensure the time slot is set first
      setShowModal(false);
      // You can now trigger the booking process separately, for example, via a button or automatically
      handleBookingWithDelay();
    }
  };

  const handleBookingWithDelay = () => {
    setTimeout(handleBooking, 0); // Ensure handleBooking is called after the state update
  };

  const handleBooking = () => {
    if (!selectedTimeSlot) {
      alert("Please select a time slot before booking.");
      return;
    }

    const businessId = userId;
    console.log("Starting booking process...");

    fetch(`/api/profile/business/bookings/regular`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        business_id: businessId,
        user_id: localStorage.getItem("userId"),
        date: selectedDate.toISOString().split("T")[0],
        time_slot: selectedTimeSlot,
        num_of_people: category === "Hall" ? numOfPeople : null,
        total_price: totalPrice,
      }),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log("Booking response:", data);
        if (data.message === "Booking created successfully") {
          setEvents((prevEvents) => [
            ...prevEvents,
            {
              start: new Date(selectedDate),
              end: new Date(selectedDate),
              title: `Booked: ${selectedTimeSlot}`,
              className:
                selectedTimeSlot === "day"
                  ? "rbc-event-day"
                  : "rbc-event-night",
            },
          ]);
          setShowMealsSelection(false);
          setSelectedDate(null);
          setSelectedTimeSlot(null);
          setNumOfPeople(""); // Clear the number of people input
          setTotalPrice(0); // Reset the total price
        } else {
          alert(data.message);
        }
      })
      .catch((error) => {
        console.error("Error storing date selection:", error.message);
        alert("Error storing date selection. Please try again later.");
      });
  };

  if (loading) {
    return <p>Loading...</p>;
  }

  const dayBooking = bookingStatus.some(
    (booking) => booking.time_slot === "day"
  );
  const nightBooking = bookingStatus.some(
    (booking) => booking.time_slot === "night"
  );

  return (
    <div className='dates'>
      <NavbarBooking userId={userId} />
      <h2>Booked Dates</h2>
      <Calendar
        localizer={localizer}
        events={events}
        selectable
        onSelectSlot={handleSelectSlot}
        defaultView='month'
        views={["month", "agenda"]}
        style={{ height: 500 }}
        components={{
          toolbar: (props) => (
            <div className='rbc-toolbar'>
              <span className='rbc-btn-group'>
                <button type='button' onClick={() => props.onNavigate("PREV")}>
                  Back
                </button>
                <button type='button' onClick={() => props.onNavigate("TODAY")}>
                  Today
                </button>
                <button type='button' onClick={() => props.onNavigate("NEXT")}>
                  Next
                </button>
              </span>
            </div>
          ),
        }}
      />
      {showModal && (
        <div className='modal'>
          <div className='modal-content'>
            <h3>
              Select Time Slot for {moment(selectedDate).format("MMMM Do YYYY")}
            </h3>
            {category === "Hall" && (
              <div>
                <label>Number of People:</label>
                <input
                  type='number'
                  value={numOfPeople}
                  onChange={(e) => setNumOfPeople(e.target.value)}
                  min={minGuests || 1}
                  max={hallCapacity || 1}
                  required
                />
              </div>
            )}
            {!dayBooking && (
              <button onClick={() => handleTimeSelection("day")}>
                Book Day Slot
              </button>
            )}
            {!nightBooking && (
              <button onClick={() => handleTimeSelection("night")}>
                Book Night Slot
              </button>
            )}
            <button onClick={() => setShowModal(false)}>Cancel</button>
          </div>
        </div>
      )}

      <Modal
        isOpen={showMealsSelection}
        onClose={() => setShowMealsSelection(false)}
      >
        <h3>Select a Meal for Your Event</h3>
        <div className='meals-list'>
          {exclusiveMeal && (
            <div className='meal-option'>
              <input
                type='radio'
                id={`exclusive-${exclusiveMeal.id}`}
                value={exclusiveMeal.id}
                checked={selectedMeals.includes(exclusiveMeal)}
                onChange={() => handleMealSelection(exclusiveMeal, true)}
              />
              <label htmlFor={`exclusive-${exclusiveMeal.id}`}>
                {exclusiveMeal.name} - ${exclusiveMeal.price}
              </label>
            </div>
          )}
          {regularMeals.map((meal) => (
            <div key={meal.id} className='meal-option'>
              <input
                type='radio'
                id={`regular-${meal.id}`}
                value={meal.id}
                checked={selectedMeals.includes(meal)}
                onChange={() => handleMealSelection(meal, false)}
              />
              <label htmlFor={`regular-${meal.id}`}>
                {meal.name} - ${meal.price}
              </label>
            </div>
          ))}
        </div>
        <button onClick={calculateTotalPrice}>Calculate Total Price</button>
        <h4>Total Price: ${totalPrice}</h4>
        <button onClick={handleBooking}>Confirm and Book</button>
      </Modal>
    </div>
  );
};

export default Dates;
