import React, { useState } from "react";

const PhotoGallerySection = () => {
  const [photos, setPhotos] = useState([]);
  const userId = localStorage.getItem("userId");

  useEffect(() => {
    fetch(`http://localhost:8801/api/profile/users/${userId}/photos`)
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        console.log("Received photos:", data.photos); // Log the received photo URLs
        setPhotos(data.photos || []); // Set the photos state with the received data
      })
      .catch((error) => {
        console.error("Error fetching photos:", error);
      });
  }, [userId]);


  return (
    <div>
      <h2>Photos</h2>
      <div className='photo-gallery'>
        {photos.length > 0 ? (
          photos.map((photo, index) => (
            <div key={index}>
              <img
                src={photo}
                alt={`Photo ${index + 1}`}
                className='business-photo'
              />
              <p>Photo {index + 1}</p>
            </div>
          ))
        ) : (
          <p>No photos available. Upload some photos to display them here.</p>
        )}
      </div>
    </div>
  );
};

export default PhotoGallerySection;
