import React, { useState, useEffect } from 'react';
import '../styles/Services.css';

const Services = () => {
  const [exclusiveMeal, setExclusiveMeal] = useState(null);
  const [regularMeals, setRegularMeals] = useState([]);
  const [newMeal, setNewMeal] = useState({ name: '', description: '', image: null, price: '' });
  const [showExclusiveForm, setShowExclusiveForm] = useState(false);
  const [showRegularForm, setShowRegularForm] = useState(false);
  const [editMode, setEditMode] = useState({ isEditing: false, mealType: null, mealId: null });
  const [showPhotoUpdateForm, setShowPhotoUpdateForm] = useState(false);

  useEffect(() => {
    fetch('/api/meals/regular')
      .then(response => response.json())
      .then(data => setRegularMeals(data))
      .catch(error => console.error('Error fetching regular meals:', error));

    fetch('/api/meals/exclusive')
      .then(response => response.json())
      .then(data => setExclusiveMeal(data[0]))
      .catch(error => console.error('Error fetching exclusive meal:', error));
  }, []);

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    setNewMeal((prevMeal) => ({
      ...prevMeal,
      image: file
    }));
  };

  const startEditMeal = (mealType, meal) => {
    setNewMeal(meal);
    setEditMode({ isEditing: true, mealType, mealId: meal.id });
    if (mealType === 'exclusive') {
      setShowExclusiveForm(true);
    } else {
      setShowRegularForm(true);
    }
  };

  const startPhotoUpdate = (mealType, mealId) => {
    setEditMode({ isEditing: true, mealType, mealId });
    setShowPhotoUpdateForm(true);
  };

  const saveUpdatedMealDetails = () => {
    const data = {
      name: newMeal.name,
      description: newMeal.description,
      price: newMeal.price,
    };

    const apiPath = editMode.mealType === 'exclusive' ? `/api/meals/exclusive/${editMode.mealId}` : `/api/meals/regular/${editMode.mealId}`;

    fetch(apiPath, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(data),
    })
    .then(response => response.json())
    .then(data => {
      console.log(`${editMode.mealType} meal details updated:`, data);
      if (editMode.mealType === 'exclusive') {
        setExclusiveMeal({ ...exclusiveMeal, ...newMeal });
      } else {
        setRegularMeals(regularMeals.map(meal => (meal.id === editMode.mealId ? { ...meal, ...newMeal } : meal)));
      }
      cancelEdit(); // Reset form and edit mode
    })
    .catch(error => console.error(`Error updating ${editMode.mealType} meal details:`, error));
  };

  const saveUpdatedMealPhoto = () => {
    const formData = new FormData();
    formData.append('image', newMeal.image);

    const apiPath = editMode.mealType === 'exclusive' ? `/api/meals/exclusive/photo/${editMode.mealId}` : `/api/meals/regular/photo/${editMode.mealId}`;

    fetch(apiPath, {
      method: 'PUT',
      body: formData,
    })
    .then(response => response.json())
    .then(data => {
      console.log(`${editMode.mealType} meal photo updated:`, data);
      if (editMode.mealType === 'exclusive') {
        setExclusiveMeal({ ...exclusiveMeal, image: newMeal.image.name });
      } else {
        setRegularMeals(regularMeals.map(meal => (meal.id === editMode.mealId ? { ...meal, image: newMeal.image.name } : meal)));
      }
      cancelEdit(); // Reset form and edit mode
    })
    .catch(error => console.error(`Error updating ${editMode.mealType} meal photo:`, error));
  };

  const cancelEdit = () => {
    setNewMeal({ name: '', description: '', image: null, price: '' });
    setEditMode({ isEditing: false, mealType: null, mealId: null });
    setShowExclusiveForm(false);
    setShowRegularForm(false);
    setShowPhotoUpdateForm(false);
  };

  const deleteExclusiveMeal = () => {
    fetch(`/api/meals/exclusive/${exclusiveMeal.id}`, {
      method: 'DELETE',
    })
    .then(response => response.json())
    .then(data => {
      console.log('Exclusive meal deleted:', data);
      setExclusiveMeal(null);
    })
    .catch(error => console.error('Error deleting exclusive meal:', error));
  };

  const deleteRegularMeal = (mealId) => {
    fetch(`/api/meals/regular/${mealId}`, {
      method: 'DELETE',
    })
    .then(response => response.json())
    .then(data => {
      console.log('Regular meal deleted:', data);
      setRegularMeals(regularMeals.filter(m => m.id !== mealId));
    })
    .catch(error => console.error('Error deleting regular meal:', error));
  };
  const addExclusiveMeal = () => {
    if (newMeal.name && newMeal.description && newMeal.image && newMeal.price) {
      const formData = new FormData();
      formData.append('name', newMeal.name);
      formData.append('description', newMeal.description);
      formData.append('price', newMeal.price);
      formData.append('image', newMeal.image);

      fetch('/api/meals/exclusive', {
        method: 'POST',
        body: formData,
      })
      .then(response => response.json())
      .then(data => {
        console.log('Exclusive meal added:', data);
        setExclusiveMeal(newMeal);
        setNewMeal({ name: '', description: '', image: null, price: '' });
        setShowExclusiveForm(false);
      })
      .catch(error => console.error('Error adding exclusive meal:', error));
    } else {
      alert("Please fill in all fields for the exclusive meal.");
    }
  };

  const addRegularMeal = () => {
    if (newMeal.name && newMeal.description && newMeal.image && newMeal.price) {
      const formData = new FormData();
      formData.append('name', newMeal.name);
      formData.append('description', newMeal.description);
      formData.append('price', newMeal.price);
      formData.append('image', newMeal.image);

      fetch('/api/meals/regular', {
        method: 'POST',
        body: formData,
      })
      .then(response => response.json())
      .then(data => {
        console.log('Regular meal added:', data);
        setRegularMeals([...regularMeals, newMeal]);
        setNewMeal({ name: '', description: '', image: null, price: '' });
        setShowRegularForm(false);
      })
      .catch(error => console.error('Error adding regular meal:', error));
    } else {
      alert("Please fill in all fields for the regular meal.");
    }
  return (
    <div className="profile-container">
      <h1>Hall Services</h1>

      <h2>Exclusive Meal</h2>
      {!showExclusiveForm && !editMode.isEditing && (
        <button onClick={() => setShowExclusiveForm(true)}>Add Exclusive Meal</button>
      )}
      {(showExclusiveForm || (editMode.isEditing && editMode.mealType === 'exclusive')) && (
        <div>
          <input
            type="text"
            placeholder="Meal Name"
            value={newMeal.name || ''}
            onChange={(e) => setNewMeal({ ...newMeal, name: e.target.value })}
          />
          <input
            type="text"
            placeholder="Description"
            value={newMeal.description || ''}
            onChange={(e) => setNewMeal({ ...newMeal, description: e.target.value })}
          />
          <input
            type="number"
            placeholder="Price"
            value={newMeal.price || ''}
            onChange={(e) => setNewMeal({ ...newMeal, price: e.target.value })}
          />
          {editMode.isEditing && (
            <div>
              <button onClick={saveUpdatedMealDetails}>Save Details</button>
              <button onClick={cancelEdit}>Cancel</button>
            </div>
          )}
          {!editMode.isEditing && (
            <div>
              <button onClick={addExclusiveMeal}>Save Exclusive Meal</button>
              <button onClick={() => setShowExclusiveForm(false)}>Cancel</button>
            </div>
          )}
        </div>
      )}

      {showPhotoUpdateForm && (
        <div>
          <input
            type="file"
            onChange={handleImageUpload}
          />
          <button onClick={saveUpdatedMealPhoto}>Save Photo</button>
          <button onClick={cancelEdit}>Cancel</button>
        </div>
      )}

      {exclusiveMeal && !editMode.isEditing && (
        <div className="meal-container">
          <img src={`/uploads/${exclusiveMeal.image}`} alt={exclusiveMeal.name} style={{ width: '100px', height: '100px' }} />
          <h3>{exclusiveMeal.name}</h3>
          <p>{exclusiveMeal.description}</p>
          <p>{exclusiveMeal.price}</p>
          <div className="dropdown">
            <button className="dropbtn">⋮</button>
            <div className="dropdown-content">
              <button onClick={() => startEditMeal('exclusive', exclusiveMeal)}>Update Details</button>
              <button onClick={() => startPhotoUpdate('exclusive', exclusiveMeal.id)}>Update Photo</button>
              <button onClick={deleteExclusiveMeal}>Remove</button>
            </div>
          </div>
        </div>
      )}

      <h2>Regular Meals</h2>
      {!showRegularForm && !editMode.isEditing && (
        <button onClick={() => setShowRegularForm(true)}>Add Regular Meal</button>
      )}
      {(showRegularForm || (editMode.isEditing && editMode.mealType === 'regular')) && (
        <div>
          <input
            type="text"
            placeholder="Meal Name"
            value={newMeal.name || ''}
            onChange={(e) => setNewMeal({ ...newMeal, name: e.target.value })}
          />
          <input
            type="text"
            placeholder="Description"
            value={newMeal.description || ''}
            onChange={(e) => setNewMeal({ ...newMeal, description: e.target.value })}
          />
          <input
            type="number"
            placeholder="Price"
            value={newMeal.price || ''}
            onChange={(e) => setNewMeal({ ...newMeal, price: e.target.value })}
          />
          {editMode.isEditing && (
            <div>
              <button onClick={saveUpdatedMealDetails}>Save Details</button>
              <button onClick={cancelEdit}>Cancel</button>
            </div>
          )}
          {!editMode.isEditing && (
            <div>
              <button onClick={addRegularMeal}>Save Regular Meal</button>
              <button onClick={() => setShowRegularForm(false)}>Cancel</button>
            </div>
          )}
        </div>
      )}

      {showPhotoUpdateForm && editMode.mealType === 'regular' && (
        <div>
          <input
            type="file"
            onChange={handleImageUpload}
          />
          <button onClick={saveUpdatedMealPhoto}>Save Photo</button>
          <button onClick={cancelEdit}>Cancel</button>
        </div>
      )}

      <ul>
        {regularMeals.map((meal, index) => (
          <li key={index} className="meal-container">
            <img src={`/uploads/${meal.image}`} alt={meal.name} style={{ width: '100px', height: '100px' }} />
            <h3>{meal.name}</h3>
            <p>{meal.description}</p>
            <p>{meal.price}</p>
            <div className="dropdown">
              <button className="dropbtn">⋮</button>
              <div className="dropdown-content">
                <button onClick={() => startEditMeal('regular', meal)}>Update Details</button>
                <button onClick={() => startPhotoUpdate('regular', meal.id)}>Update Photo</button>
                <button onClick={() => deleteRegularMeal(meal.id)}>Remove</button>
              </div>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};
}
export default Services;
