// src/components/Dates.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import '../styles/BookingCalendar.css';
import NavbarBooking from '../components/NavbarBooking'; // Import NavbarBooking
import '../styles/Dates.css';

const localizer = momentLocalizer(moment);

const Dates = () => {
  const { userId } = useParams();
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedDate, setSelectedDate] = useState(null);
  const [selectedTimeSlot, setSelectedTimeSlot] = useState(null);
  const [showModal, setShowModal] = useState(false);

  const userType = localStorage.getItem('userType'); // Get the user type from localStorage

  useEffect(() => {
    const fetchBookedDates = async () => {
      try {
        const response = await fetch(`/api/profile/business/bookings/${userId}/booked-dates`);
        if (!response.ok) {
          throw new Error('Failed to fetch booked dates');
        }
        const data = await response.json();
        const formattedEvents = data.bookedDates.map((event) => ({
          start: new Date(event.booking_date),
          end: new Date(event.booking_date),
          title: `Booked: ${event.time_slot}`,
        }));
        setEvents(formattedEvents);
      } catch (error) {
        console.error('Error fetching booked dates:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchBookedDates();
  }, [userId]);

  const handleSelectSlot = ({ start }) => {
    const now = new Date();
    if (start <= now) {
      alert('Please select a future date.');
      return;
    }
    setSelectedDate(start);
    setShowModal(true);
  };

  const handleTimeSelection = (time) => {
    setSelectedTimeSlot(time);
    handleBooking(time);
  };

  const handleBooking = (timeSlot) => {
    const businessId = userId;

    fetch(`/api/bookings/regular`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        business_id: businessId,
        date: selectedDate.toISOString().split('T')[0],
        time_slot: timeSlot,
        firstName: 'N/A', // Placeholder
        lastName: 'N/A',  // Placeholder
        idNum: 'N/A',     // Placeholder
        phoneNumber: 'N/A' // Placeholder
      }),
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.message === 'Booking created successfully') {
          setEvents((prevEvents) => [
            ...prevEvents,
            {
              start: new Date(selectedDate),
              end: new Date(selectedDate),
              title: `Booked: ${timeSlot}`,
            },
          ]);
          setShowModal(false);
          setSelectedDate(null);
          setSelectedTimeSlot(null);
        } else {
          alert(data.message);
        }
      })
      .catch((error) => console.error('Error storing date selection:', error));
  };

  if (loading) {
    return <p>Loading...</p>;
  }

  return (
    <div className="dates">
      <NavbarBooking userId={userId} /> {/* Add NavbarBooking */}
      <h2>Booked Dates</h2>
      <Calendar
        localizer={localizer}
        events={events}
        selectable={userType === 'regular'} // Allow selecting only for regular users
        onSelectSlot={handleSelectSlot}
        defaultView="month"
        views={['month', 'agenda']}
        style={{ height: 500 }}
      />
      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <h3>Select Time Slot for {moment(selectedDate).format('MMMM Do YYYY')}</h3>
            <button onClick={() => handleTimeSelection('day')}>
              Day Slot
            </button>
            <button onClick={() => handleTimeSelection('night')}>
              Night Slot
            </button>
            <button onClick={() => setShowModal(false)}>Cancel</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dates;
