// src/components/BigCalendarComponent.js
import moment from "moment";
import React, { useState } from "react";
import { Calendar, momentLocalizer } from "react-big-calendar";
import "react-big-calendar/lib/css/react-big-calendar.css";
import "../styles/BigCalendarComponent.css"; // Create and style this CSS file as needed

const localizer = momentLocalizer(moment);

const BigCalendarComponent = () => {
  const [events, setEvents] = useState([]);
  const [selectedDate, setSelectedDate] = useState(null);
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    idNum: "",
    phoneNumber: "",
  });

  const handleSelectSlot = ({ start }) => {
    setSelectedDate(start);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({ ...prevData, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert(
      `Date: ${selectedDate}, First Name: ${formData.firstName}, Last Name: ${formData.lastName}, ID: ${formData.idNum}, Phone: ${formData.phoneNumber}`
    );
    // Reset form and selected date
    setFormData({ firstName: "", lastName: "", idNum: "", phoneNumber: "" });
    setSelectedDate(null);
  };

  return (
    <div className='bigcalendar-container'>
      <Calendar
        localizer={localizer}
        events={events}
        selectable
        onSelectSlot={handleSelectSlot}
        style={{ height: 500 }}
      />
      {selectedDate && (
        <form className='date-form' onSubmit={handleSubmit}>
          <h3>Selected Date: {selectedDate.toDateString()}</h3>
          <div className='form-group'>
            <label>First Name</label>
            <input
              type='text'
              name='firstName'
              value={formData.firstName}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className='form-group'>
            <label>Last Name</label>
            <input
              type='text'
              name='lastName'
              value={formData.lastName}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className='form-group'>
            <label>ID Number</label>
            <input
              type='text'
              name='idNum'
              value={formData.idNum}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className='form-group'>
            <label>Phone Number</label>
            <input
              type='text'
              name='phoneNumber'
              value={formData.phoneNumber}
              onChange={handleInputChange}
              required
            />
          </div>
          <button type='submit'>Submit</button>
        </form>
      )}
    </div>
  );
};

export default BigCalendarComponent;
