// src/components/Dates.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import '../styles/BookingCalendar.css';
import NavbarBooking from '../components/NavbarBooking'; // Import NavbarBooking
import '../styles/Dates.css';

const localizer = momentLocalizer(moment);

const Dates = () => {
  const { userId } = useParams(); // Get the userId from the URL parameters
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchBookedDates = async () => {
      try {
        const response = await fetch(`/api/profile/business/bookings/${userId}/booked-dates`);
        if (!response.ok) {
          throw new Error('Failed to fetch booked dates');
        }
        const data = await response.json();
        const formattedEvents = data.bookedDates.map((event) => ({
          start: new Date(event.booking_date),
          end: new Date(event.booking_date),
          title: `Booked: ${event.time_slot}`,
        }));
        setEvents(formattedEvents);
      } catch (error) {
        console.error('Error fetching booked dates:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchBookedDates();
  }, [userId]); // Re-run the effect if userId changes

  if (loading) {
    return <p>Loading...</p>;
  }

  return (
    <div className="dates">
      <NavbarBooking userId={userId} /> {/* Add NavbarBooking */}
      <h2>Booked Dates</h2>
      <Calendar
        localizer={localizer}
        events={events}

        defaultView="month"
        views={['month', 'agenda']}
        style={{ height: 500 }}
      />
    </div>
  );
};

export default Dates;
