import moment from "moment";
import React, { useEffect, useState } from "react";
import { Calendar, momentLocalizer } from "react-big-calendar";
import "react-big-calendar/lib/css/react-big-calendar.css";
import { useParams } from "react-router-dom";
import "../styles/Dates.css";
import NavbarBooking from "./NavbarBooking";

const localizer = momentLocalizer(moment);

const Dates = () => {
  const { userId } = useParams();
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedDate, setSelectedDate] = useState(null);
  const [selectedTimeSlot, setSelectedTimeSlot] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [showMealsSelection, setShowMealsSelection] = useState(false);
  const [bookingStatus, setBookingStatus] = useState([]);
  const [userType, setUserType] = useState(null);
  const [numOfPeople, setNumOfPeople] = useState("");
  const [hallCapacity, setHallCapacity] = useState(null);
  const [minGuests, setMinGuests] = useState(null);
  const [exclusiveMeal, setExclusiveMeal] = useState(null);
  const [regularMeals, setRegularMeals] = useState([]);
  const [selectedMeals, setSelectedMeals] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);
  const [pricePerEvent, setPricePerEvent] = useState(0);

  useEffect(() => {
    const fetchUserType = () => {
      const storedUserType = localStorage.getItem("userType");
      setUserType(storedUserType);
    };

    const fetchHallDetails = async () => {
      try {
        const response = await fetch(`/api/bookings/business/${userId}/capacity`);
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const data = await response.json();
        setHallCapacity(data.hallCapacity || 0);
        setMinGuests(data.minGuests || 0);
        setPricePerEvent(data.price_per_event || 0); // Set the price per event
      } catch (error) {
        console.error("Error fetching hall details:", error);
      }
    };

    fetchUserType();
    fetchHallDetails();

    const fetchBookedDates = async () => {
      try {
        const response = await fetch(
          `/api/profile/business/bookings/${userId}/booked-dates`
        );
        if (!response.ok) {
          throw new Error("Failed to fetch booked dates");
        }
        const data = await response.json();
        const formattedEvents = data.bookedDates.map((event) => ({
          start: new Date(event.booking_date),
          end: new Date(event.booking_date),
          title: `Booked: ${event.time_slot}`,
          className: event.time_slot === "day" ? "rbc-event-day" : "rbc-event-night",
        }));
        setEvents(formattedEvents);
      } catch (error) {
        console.error("Error fetching booked dates:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchBookedDates();
  }, [userId]);

  useEffect(() => {
    if (userId) {
      // Fetch regular meals by user ID
      fetch(`/api/meals/regular/user/${userId}`)
        .then((response) => response.json())
        .then((data) => setRegularMeals(data))
        .catch((error) => console.error("Error fetching regular meals:", error));

      // Fetch exclusive meal by user ID
      fetch(`/api/meals/exclusive/user/${userId}`)
        .then((response) => response.json())
        .then((data) => setExclusiveMeal(data[0]))
        .catch((error) => console.error("Error fetching exclusive meal:", error));
    }
  }, [userId]);

  const handleSelectSlot = ({ start }) => {
    const now = new Date();
    if (userType === "business") {
      alert("Business users cannot book dates.");
      return;
    }
    if (userType === null) {
      alert("You have to sign in first to book a date!");
      return;
    }
    if (start <= now) {
      alert("Please select a future date.");
      return;
    }
    setSelectedDate(start);
    const selectedDateString = start.toISOString().split("T")[0];

    fetch(`/api/profile/business/bookings/check/${userId}/${selectedDateString}`)
      .then((response) => response.json())
      .then((data) => {
        if (!Array.isArray(data.bookedSlots)) {
          console.error("Invalid response:", data);
          return;
        }
        setBookingStatus(data.bookedSlots);
        const dayBooking = data.bookedSlots.some(
          (booking) => booking.time_slot === "day"
        );
        const nightBooking = data.bookedSlots.some(
          (booking) => booking.time_slot === "night"
        );

        if (dayBooking && nightBooking) {
          alert("This date is fully booked.");
        } else {
          setShowModal(true);
        }
      })
      .catch((error) => console.error("Error checking booking status:", error));
  };

  const handleMealSelection = (meal) => {
    setSelectedMeals((prevMeals) => {
      if (prevMeals.includes(meal)) {
        return prevMeals.filter((m) => m !== meal);
      } else {
        return [...prevMeals, meal];
      }
    });
  };

  const calculateTotalPrice = () => {
    const mealsPrice = selectedMeals.reduce((acc, meal) => acc + meal.price, 0);
    const total = mealsPrice * numOfPeople + pricePerEvent;
    setTotalPrice(total);
  };

  const handleTimeSelection = (time) => {
    if (!numOfPeople || numOfPeople < minGuests || numOfPeople > hallCapacity) {
      alert(
        `The number of people invited must be between ${minGuests} and ${hallCapacity}.`
      );
      return;
    }
    setSelectedTimeSlot(time);
    setShowModal(false);
    setShowMealsSelection(true);
  };

  const handleBooking = () => {
    const businessId = userId;

    fetch(`/api/profile/business/bookings/regular`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        business_id: businessId,
        user_id: localStorage.getItem("userId"),
        date: selectedDate.toISOString().split("T")[0],
        time_slot: selectedTimeSlot,
        num_of_people: numOfPeople,
        total_price: totalPrice, // Add total price to the booking
      }),
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.message === "Booking created successfully") {
          setEvents((prevEvents) => [
            ...prevEvents,
            {
              start: new Date(selectedDate),
              end: new Date(selectedDate),
              title: `Booked: ${selectedTimeSlot}`,
              className: selectedTimeSlot === "day" ? "rbc-event-day" : "rbc-event-night",
            },
          ]);
          setShowMealsSelection(false);
          setSelectedDate(null);
          setSelectedTimeSlot(null);
          setNumOfPeople(""); // Clear the number of people input
          setTotalPrice(0); // Reset the total price
        } else {
          alert(data.message);
        }
      })
      .catch((error) => console.error("Error storing date selection:", error));
  };

  if (loading) {
    return <p>Loading...</p>;
  }

  const dayBooking = bookingStatus.some(
    (booking) => booking.time_slot === "day"
  );
  const nightBooking = bookingStatus.some(
    (booking) => booking.time_slot === "night"
  );

  return (
    <div className="dates">
      <NavbarBooking userId={userId} />
      <h2>Booked Dates</h2>
      <Calendar
        localizer={localizer}
        events={events}
        selectable
        onSelectSlot={handleSelectSlot}
        defaultView="month"
        views={["month", "agenda"]}
        style={{ height: 500 }}
        components={{
          toolbar: (props) => (
            <div className="rbc-toolbar">
              <span className="rbc-btn-group">
                <button type="button" onClick={() => props.onNavigate("PREV")}>
                  Back
                </button>
                <button type="button" onClick={() => props.onNavigate("TODAY")}>
                  Today
                </button>
                <button type="button" onClick={() => props.onNavigate("NEXT")}>
                  Next
                </button>
              </span>
            </div>
          ),
        }}
      />
      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <h3>
              Select Time Slot for {moment(selectedDate).format("MMMM Do YYYY")}
            </h3>
            <div>
              <label>Number of People:</label>
              <input
                type="number"
                value={numOfPeople}
                onChange={(e) => setNumOfPeople(e.target.value)}
                min={minGuests || 1}
                max={hallCapacity || 1}
                required
              />
            </div>
            {!dayBooking && (
              <button onClick={() => handleTimeSelection("day")}>
                Book Day Slot
              </button>
            )}
            {!nightBooking && (
              <button onClick={() => handleTimeSelection("night")}>
                Book Night Slot
              </button>
            )}
            <button onClick={() => setShowModal(false)}>Cancel</button>
          </div>
        </div>
      )}

      {showMealsSelection && (
        <div className="meals-selection">
          <h3>Select Meals for Your Event</h3>
          <div className="meals-list">
            {exclusiveMeal && (
              <div className="meal-option">
                <img
                  src={`/uploads/${exclusiveMeal.image}`}
                  alt={exclusiveMeal.name}
                  style={{ width: "100px", height: "100px" }}
                />
                <input
                  type="checkbox"
                  id={`exclusive-${exclusiveMeal.id}`}
                  value={exclusiveMeal.id}
                  onChange={() => handleMealSelection(exclusiveMeal)}
                />
                <label htmlFor={`exclusive-${exclusiveMeal.id}`}>
                  {exclusiveMeal.name} - ${exclusiveMeal.price}
                </label>
              </div>
            )}
            {regularMeals.map((meal) => (
              <div key={meal.id} className="meal-option">
                <img
                  src={`/uploads/${meal.image}`}
                  alt={meal.name}
                  style={{ width: "100px", height: "100px" }}
                />
                <input
                  type="checkbox"
                  id={`regular-${meal.id}`}
                  value={meal.id}
                  onChange={() => handleMealSelection(meal)}
                />
                <label htmlFor={`regular-${meal.id}`}>
                  {meal.name} - ${meal.price}
                </label>
              </div>
            ))}
          </div>
          <button onClick={calculateTotalPrice}>Calculate Total Price</button>
          <h4>Total Price: ${totalPrice}</h4>
          <button onClick={handleBooking}>Confirm and Book</button>
          <button onClick={() => setShowMealsSelection(false)}>Cancel</button>
        </div>
      )}
    </div>
  );
};

export default Dates;
