import React, { useEffect, useState } from "react";
import { Calendar, momentLocalizer } from "react-big-calendar";
import moment from "moment";
import Services from "./Services";
import "react-big-calendar/lib/css/react-big-calendar.css";
import "../styles/Dates.css";
import NavbarBooking from "./NavbarBooking";

const localizer = momentLocalizer(moment);

const Dates = () => {
  const { userId } = useParams();
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedDate, setSelectedDate] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [showMealPopup, setShowMealPopup] = useState(false);
  const [bookingStatus, setBookingStatus] = useState([]);
  const [userType, setUserType] = useState(null);
  const [selectedMeals, setSelectedMeals] = useState([]);
  const [numOfPeople, setNumOfPeople] = useState(0);
  const [pricePerEvent, setPricePerEvent] = useState(0);

  useEffect(() => {
    const fetchUserType = () => {
      const storedUserType = localStorage.getItem("userType");
      setUserType(storedUserType);
    };

    const fetchBusinessDetails = async () => {
      try {
        const response = await fetch(`/api/bookings/business/${userId}/capacity`);
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const data = await response.json();
        setPricePerEvent(data.price_per_event || 0); // Set price per event
      } catch (error) {
        console.error("Error fetching business details:", error);
      }
    };

    fetchUserType();
    fetchBusinessDetails();

    const fetchBookedDates = async () => {
      try {
        const response = await fetch(
          `/api/profile/business/bookings/${userId}/booked-dates`
        );
        if (!response.ok) {
          throw new Error("Failed to fetch booked dates");
        }
        const data = await response.json();
        const formattedEvents = data.bookedDates.map((event) => ({
          start: new Date(event.booking_date),
          end: new Date(event.booking_date),
          title: `Booked: ${event.time_slot}`,
          className:
            event.time_slot === "day" ? "rbc-event-day" : "rbc-event-night",
        }));
        setEvents(formattedEvents);
      } catch (error) {
        console.error("Error fetching booked dates:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchBookedDates();
  }, [userId]);

  const handleSelectSlot = ({ start }) => {
    const now = new Date();
    if (userType === "business") {
      alert("Business users cannot book dates.");
      return;
    }
    if (userType === null) {
      alert("You have to sign in first to book a date!");
      return;
    }
    if (start <= now) {
      alert("Please select a future date.");
      return;
    }
    setSelectedDate(start);
    const selectedDateString = start.toISOString().split("T")[0];

    fetch(`/api/profile/business/bookings/check/${userId}/${selectedDateString}`)
      .then((response) => response.json())
      .then((data) => {
        if (!Array.isArray(data.bookedSlots)) {
          console.error("Invalid response:", data);
          return;
        }
        setBookingStatus(data.bookedSlots);
        const dayBooking = data.bookedSlots.some(
          (booking) => booking.time_slot === "day"
        );
        const nightBooking = data.bookedSlots.some(
          (booking) => booking.time_slot === "night"
        );

        if (dayBooking && nightBooking) {
          alert("This date is fully booked.");
        } else {
          setShowMealPopup(true); // Show the meal selection popup
        }
      })
      .catch((error) => console.error("Error checking booking status:", error));
  };

  const handleMealSelection = (meal) => {
    setSelectedMeals((prevSelectedMeals) => {
      const isAlreadySelected = prevSelectedMeals.find(
        (selectedMeal) => selectedMeal.id === meal.id
      );

      if (isAlreadySelected) {
        return prevSelectedMeals.filter((selectedMeal) => selectedMeal.id !== meal.id);
      } else {
        return [...prevSelectedMeals, meal];
      }
    });
  };

  const handleConfirmMeals = () => {
    setShowMealPopup(false);
    setShowModal(true);
  };

  const handleSkipMeals = () => {
    setShowMealPopup(false);
    setShowModal(true);
  };

  const handleBooking = (timeSlot) => {
    if (userType === "business") {
      alert("Business users cannot book dates.");
      return;
    }

    const businessId = userId;
    const mealsTotalPrice = selectedMeals.reduce((acc, meal) => acc + meal.price, 0) * numOfPeople;
    const totalPrice = mealsTotalPrice + pricePerEvent;

    fetch(`/api/profile/business/bookings/regular`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        business_id: businessId,
        user_id: localStorage.getItem("userId"),
        date: selectedDate.toISOString().split("T")[0],
        time_slot: timeSlot,
        selectedMeals,
        num_of_people: numOfPeople,
        total_price: totalPrice, // Send the calculated total price
      }),
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.message === "Booking created successfully") {
          setEvents((prevEvents) => [
            ...prevEvents,
            {
              start: new Date(selectedDate),
              end: new Date(selectedDate),
              title: `Booked: ${timeSlot}`,
              className: timeSlot === "day" ? "rbc-event-day" : "rbc-event-night",
            },
          ]);
          setShowModal(false);
          setSelectedDate(null);
          setSelectedTimeSlot(null);
          setSelectedMeals([]); // Clear selected meals
          setNumOfPeople(0); // Clear number of people
        } else {
          alert(data.message);
        }
      })
      .catch((error) => console.error("Error storing date selection:", error));
  };

  if (loading) {
    return <p>Loading...</p>;
  }

  const dayBooking = bookingStatus.some(
    (booking) => booking.time_slot === "day"
  );
  const nightBooking = bookingStatus.some(
    (booking) => booking.time_slot === "night"
  );

  return (
    <div className="dates">
      <NavbarBooking userId={userId} />
      <h2>Booked Dates</h2>
      <Calendar
        localizer={localizer}
        events={events}
        selectable
        onSelectSlot={handleSelectSlot}
        defaultView="month"
        views={["month", "agenda"]}
        style={{ height: 500 }}
        components={{
          toolbar: (props) => (
            <div className="rbc-toolbar">
              <span className="rbc-btn-group">
                <button type="button" onClick={() => props.onNavigate("PREV")}>
                  Back
                </button>
                <button type="button" onClick={() => props.onNavigate("TODAY")}>
                  Today
                </button>
                <button type="button" onClick={() => props.onNavigate("NEXT")}>
                  Next
                </button>
              </span>
            </div>
          ),
        }}
      />

      {showMealPopup && (
        <Services
          onSelectMeals={handleMealSelection}
          onConfirmMeals={handleConfirmMeals}
          onSkipMeals={handleSkipMeals}
          selectedMeals={selectedMeals} // Pass selected meals to Services component
        />
      )}

      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <h3>
              Select Time Slot for {moment(selectedDate).format("MMMM Do YYYY")}
            </h3>
            <div>
              <label>Number of People:</label>
              <input
                type="number"
                value={numOfPeople}
                onChange={(e) => setNumOfPeople(e.target.value)}
                min={1}
                required
              />
            </div>
            {!dayBooking && (
              <button onClick={() => handleBooking("day")}>Day Slot</button>
            )}
            {!nightBooking && (
              <button onClick={() => handleBooking("night")}>Night Slot</button>
            )}
            <button onClick={() => setShowModal(false)}>Cancel</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dates;
