import React, { useEffect, useState } from "react";
import "../styles/Services.css";

const Services = () => {
  const [exclusiveMeal, setExclusiveMeal] = useState(null);
  const [regularMeals, setRegularMeals] = useState([]);
  const [newMeal, setNewMeal] = useState({
    name: "",
    description: "",
    image: null,
    price: "",
  });
  const [showExclusiveForm, setShowExclusiveForm] = useState(false);
  const [showRegularForm, setShowRegularForm] = useState(false);

  useEffect(() => {
    fetch("/api/meals/regular")
      .then((response) => response.json())
      .then((data) => setRegularMeals(data))
      .catch((error) => console.error("Error fetching regular meals:", error));

    fetch("/api/meals/exclusive")
      .then((response) => response.json())
      .then((data) => setExclusiveMeal(data[0]))
      .catch((error) => console.error("Error fetching exclusive meal:", error));
  }, []);

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    setNewMeal((prevMeal) => ({
      ...prevMeal,
      image: file, // Store the actual file object
    }));
  };

  const addExclusiveMeal = () => {
    if (newMeal.name && newMeal.description && newMeal.image && newMeal.price) {
      const formData = new FormData();
      formData.append("name", newMeal.name);
      formData.append("description", newMeal.description);
      formData.append("price", newMeal.price);
      formData.append("image", newMeal.image);

      fetch("/api/meals/exclusive", {
        method: "POST",
        body: formData,
      })
        .then((response) => response.json())
        .then((data) => {
          console.log("Exclusive meal added:", data);
          setExclusiveMeal(newMeal);
          setNewMeal({ name: "", description: "", image: null, price: "" });
          setShowExclusiveForm(false);
        })
        .catch((error) => console.error("Error adding exclusive meal:", error));
    } else {
      alert("Please fill in all fields for the exclusive meal.");
    }
  };

  const updateExclusiveMeal = () => {
    if (
      exclusiveMeal.name &&
      exclusiveMeal.description &&
      exclusiveMeal.price
    ) {
      const formData = new FormData();
      formData.append("name", exclusiveMeal.name);
      formData.append("description", exclusiveMeal.description);
      formData.append("price", exclusiveMeal.price);
      if (exclusiveMeal.image) {
        formData.append("image", exclusiveMeal.image);
      }

      fetch(`/api/meals/exclusive/${exclusiveMeal.id}`, {
        method: "PUT",
        body: formData,
      })
        .then((response) => response.json())
        .then((data) => {
          console.log("Exclusive meal updated:", data);
          // Optionally refetch or update state
        })
        .catch((error) =>
          console.error("Error updating exclusive meal:", error)
        );
    }
  };

  const deleteExclusiveMeal = () => {
    fetch(`/api/meals/exclusive/${exclusiveMeal.id}`, {
      method: "DELETE",
    })
      .then((response) => response.json())
      .then((data) => {
        console.log("Exclusive meal deleted:", data);
        setExclusiveMeal(null); // Clear the meal from the state
      })
      .catch((error) => console.error("Error deleting exclusive meal:", error));
  };

  const addRegularMeal = () => {
    if (newMeal.name && newMeal.description && newMeal.image && newMeal.price) {
      const formData = new FormData();
      formData.append("name", newMeal.name);
      formData.append("description", newMeal.description);
      formData.append("price", newMeal.price);
      formData.append("image", newMeal.image);

      fetch("/api/meals/regular", {
        method: "POST",
        body: formData,
      })
        .then((response) => response.json())
        .then((data) => {
          console.log("Regular meal added:", data);
          setRegularMeals([...regularMeals, newMeal]);
          setNewMeal({ name: "", description: "", image: null, price: "" });
          setShowRegularForm(false);
        })
        .catch((error) => console.error("Error adding regular meal:", error));
    } else {
      alert("Please fill in all fields for the regular meal.");
    }
  };

  return (
    <div className='profile-container'>
      <h1>Hall Services</h1>

      <h2>Exclusive Meal</h2>
      {!showExclusiveForm && (
        <button onClick={() => setShowExclusiveForm(true)}>
          Add Exclusive Meal
        </button>
      )}
      {showExclusiveForm && (
        <div>
          <input
            type='text'
            placeholder='Meal Name'
            value={newMeal.name || ""}
            onChange={(e) => setNewMeal({ ...newMeal, name: e.target.value })}
          />
          <input
            type='text'
            placeholder='Description'
            value={newMeal.description || ""}
            onChange={(e) =>
              setNewMeal({ ...newMeal, description: e.target.value })
            }
          />
          <input
            type='number'
            placeholder='Price'
            value={newMeal.price || ""}
            onChange={(e) => setNewMeal({ ...newMeal, price: e.target.value })}
          />
          <input
            type='file'
            onChange={handleImageUpload} // Only pass the file to handleImageUpload
          />
          <button onClick={addExclusiveMeal}>Save Exclusive Meal</button>
          <button onClick={() => setShowExclusiveForm(false)}>Cancel</button>
        </div>
      )}

      {exclusiveMeal && (
        <div className='meal-container'>
          {exclusiveMeal.image ? (
            <img
              src={`/uploads/${exclusiveMeal.image}`}
              alt={exclusiveMeal.name}
              style={{ width: "100px", height: "100px" }}
            />
          ) : (
            <p>No image available</p> // Handle case where no image is present
          )}
          <h3>{exclusiveMeal.name}</h3>
          <p>{exclusiveMeal.description}</p>
          <p>{exclusiveMeal.price}</p>
          <div className='dropdown'>
            <button className='dropbtn'>⋮</button>
            <div className='dropdown-content'>
              <button>Update</button>
              <button>Remove</button>
            </div>
          </div>
        </div>
      )}

      <h2>Regular Meals</h2>
      {!showRegularForm && (
        <button onClick={() => setShowRegularForm(true)}>
          Add Regular Meal
        </button>
      )}
      {showRegularForm && (
        <div>
          <input
            type='text'
            placeholder='Meal Name'
            value={newMeal.name || ""}
            onChange={(e) => setNewMeal({ ...newMeal, name: e.target.value })}
          />
          <input
            type='text'
            placeholder='Description'
            value={newMeal.description || ""}
            onChange={(e) =>
              setNewMeal({ ...newMeal, description: e.target.value })
            }
          />
          <input
            type='number'
            placeholder='Price'
            value={newMeal.price || ""}
            onChange={(e) => setNewMeal({ ...newMeal, price: e.target.value })}
          />
          <input
            type='file'
            onChange={handleImageUpload} // Only pass the file to handleImageUpload
          />
          <button onClick={addRegularMeal}>Save Regular Meal</button>
          <button onClick={() => setShowRegularForm(false)}>Cancel</button>
        </div>
      )}

      <ul>
        {regularMeals.map((meal, index) => (
          <li key={index} className='meal-container'>
            {meal.image ? (
              <img
                src={`/uploads/${meal.image}`}
                alt={meal.name}
                style={{ width: "100px", height: "100px" }}
              />
            ) : (
              <p>No image available</p> // Handle case where no image is present
            )}
            <h3>{meal.name}</h3>
            <p>{meal.description}</p>
            <p>{meal.price}</p>
            <div className='dropdown'>
              <button className='dropbtn'>⋮</button>
              <div className='dropdown-content'>
                <button>Update</button>
                <button>Remove</button>
              </div>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Services;
