import React, { useContext } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Login from './pages/Login';
import Register from './components/RegularUserRegister';
import BusinessUserRegister from './components/BusinessUserRegister';
import Profile from './pages/Profile';
import BusinessProfile from './pages/BusinessProfile';
import Financial from './pages/Financial'; // Import the Financial page
import Dates from './pages/Dates'; // Import the Dates page
import Services from './pages/Services'; // Import the Services page
import AuthContext from './contexts/AuthContext';
import Layout from './components/Layout'; // Import the Layout component
import Home from './pages/Home'; // Import the Home component
import RegistrationSelection from './pages/Register';

const App = () => {
  const { isAuthenticated } = useContext(AuthContext);
  const userType = localStorage.getItem('userType'); // Get userType from localStorage

  return (
    <Router>
      <Layout>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/login" element={isAuthenticated ? <Navigate to="/profile" /> : <Login />} />
          <Route path="/register" element={isAuthenticated ? <Navigate to="/profile" /> : <Register />} />
          <Route path="/register-business" element={isAuthenticated ? <Navigate to="/profile" /> : <BusinessUserRegister />} />
          <Route
            path="/profile"
            element={
              isAuthenticated ? (
                userType === 'business' ? <BusinessProfile /> : <Profile />
              ) : (
                <Navigate to="/login" />
              )
            }
          />
          <Route path="/financial" element={<Financial />} />
          <Route path="/dates" element={<Dates />} />
          <Route path="/services" element={<Services />} />
        </Routes>
      </Layout>
    </Router>
  );
};

export default App;
