// src/components/RegularUserRegister.js
import React, { useState } from 'react';
import { API_URL } from '../constans';

const RegularUserRegister = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(`${API_URL}/api/register/regular`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, password }),
      });

      const text = await response.text();
      try {
        const data = JSON.parse(text);
        if (response.ok) {
          alert(data.message);
        } else {
          alert(data.message || 'Error registering regular user');
        }
      } catch (error) {
        console.error('Error parsing JSON:', error, 'Response:', text);
        alert('Error registering regular user. Please check the console for more details.');
      }
    } catch (error) {
      console.error('Error registering regular user:', error);
      alert('Error registering regular user. Please try again.');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Username:
        <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} />
      </label>
      <label>
        Password:
        <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
      </label>
      <button type="submit">Register</button>
    </form>
  );
};

export default RegularUserRegister;
