import { API_URL } from '../constans';

export const fetchProfile = async (userId, setUserDetails, setFormData, setProfilePhoto, userType = 'regular') => {
  try {
    const response = await fetch(`${API_URL}/api/profile/${userType}/${userId}`);
    if (!response.ok) {
      throw new Error('Network response was not ok');
    }
    const data = await response.json();
    setUserDetails(data);
    setFormData(data);
    setProfilePhoto(data.profilePhoto);
  } catch (error) {
    console.error('Error fetching user details:', error);
    setUserDetails({ error: 'Error fetching user details' });
  }
};
