import React, { useState } from "react";

const PeopleInvitedForm = ({ initialInvited, handleSave }) => {
  const [peopleInvited, setPeopleInvited] = useState(initialInvited || 0);

  const handleSubmit = (e) => {
    e.preventDefault();
    handleSave(peopleInvited);
  };

  return (
    <form onSubmit={handleSubmit}>
      <div className="form-group">
        <label>Number of People Invited:</label>
        <input
          type="number"
          value={peopleInvited}
          onChange={(e) => setPeopleInvited(e.target.value)}
        />
      </div>
      <button type="submit">Save</button>
    </form>
  );
};

export default PeopleInvitedForm;
