import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/CategoryUserProfile.css.css';

const UserProfile = () => {
  const [user, setUser] = useState(null);
  const [photos, setPhotos] = useState([]);
  const [loading, setLoading] = useState(true); // Add loading state
  const [error, setError] = useState(null); // Add error state
  const userId = localStorage.getItem('userId');
  const navigate = useNavigate();

  useEffect(() => {
    if (!userId) {
      navigate('/login'); // Redirect to login if no userId is found
      return;
    }

    const fetchUser = async () => {
      try {
        const response = await fetch(`/api/business-users/${userId}`);
        if (!response.ok) {
          throw new Error(`Failed to fetch user: ${response.statusText}`);
        }
        const data = await response.json();
        console.log('Fetched user:', data); // Debugging log
        setUser(data);
      } catch (err) {
        console.error('Error fetching user profile:', err);
        setError(err.message);
      }
    };

    fetchUser();
    setLoading(false); // Ensure loading state is updated after fetch
  }, [userId, navigate]);

  if (loading) {
    return <p>Loading user profile...</p>;
  }

  if (error) {
    return <p>Error loading user profile: {error}</p>;
  }

  if (!user) {
    return <p>User not found</p>;
  }

  return (
    <div className="user-profile">
      {user.profilePhoto ? (
        <img src={user.profilePhoto} alt={`${user.businessName} profile`} />
      ) : (
        <div className="profile-photo-placeholder">No Profile Photo</div>
      )}
      <h1>{user.businessName}</h1>
      <p>{user.address}</p>
      <p>{user.email}</p>
      <p>{user.numOfBusiness}</p>
      <p>{user.category}</p>
      {/* Add more user details as needed */}

      <div className="user-photos">
        <h2>User Photos</h2>
        {user.photos.length > 0 ? (
          user.photos.map((photo, index) => (
            <img key={index} src={photo} alt={`User photo ${index + 1}`} />
          ))
        ) : (
          <p>No photos available for this user</p>
        )}
      </div>
    </div>
  );
};

export default UserProfile;
