// src/components/BookingCalendar.js
import React, { useState, useEffect } from 'react';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import '../styles/BookingCalendar.css';

const localizer = momentLocalizer(moment);

const BookingCalendar = () => {
  const [events, setEvents] = useState([]);
  const [selectedSlot, setSelectedSlot] = useState(null);
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    idNum: '',
    phoneNumber: '',
  });

  useEffect(() => {
    // Fetch events from the API
    fetch(`${API_URL}/api/events`)
      .then(response => response.json())
      .then(data => setEvents(data))
      .catch(error => console.error('Error fetching events:', error));
  }, []);

  const handleSelectSlot = ({ start, end }) => {
    setSelectedSlot({ start, end });
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({ ...prevData, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const numOfBusiness = localStorage.getItem('numOfBusiness'); // Assuming you store this in localStorage

    fetch(`${API_URL}/api/date-selections`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ ...formData, start: selectedSlot.start, end: selectedSlot.end, numOfBusiness }),
    })
      .then((response) => response.json())
      .then((data) => {
        alert(data.message);
        // Optionally, reset form and selected slot
        setFormData({ firstName: '', lastName: '', idNum: '', phoneNumber: '' });
        setSelectedSlot(null);
        // Update events
        setEvents((prevEvents) => [...prevEvents, { start: selectedSlot.start, end: selectedSlot.end, title: `${formData.firstName} ${formData.lastName}` }]);
      })
      .catch((error) => console.error('Error storing date selection:', error));
  };

  return (
    <div className="booking-calendar-container">
      <Calendar
        localizer={localizer}
        events={events}
        selectable
        onSelectSlot={handleSelectSlot}
        defaultView="month"
        views={['month']}
        step={1440} // 24 hours in minutes
        timeslots={2}
        min={new Date(0, 0, 0, 8, 0, 0)} // Start at 8 AM
        max={new Date(0, 0, 0, 20, 0, 0)} // End at 8 PM
        components={{
          event: ({ event }) => (
            <span>
              <strong>{event.title}</strong>
              <p>{event.start.toLocaleTimeString()} - {event.end.toLocaleTimeString()}</p>
            </span>
          ),
        }}
        style={{ height: 500 }}
      />
      {selectedSlot && (
        <form className="date-form" onSubmit={handleSubmit}>
          <h3>Selected Slot: {moment(selectedSlot.start).format('MMMM Do YYYY, h:mm a')} - {moment(selectedSlot.end).format('h:mm a')}</h3>
          <div className="form-group">
            <label>First Name</label>
            <input
              type="text"
              name="firstName"
              value={formData.firstName}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Last Name</label>
            <input
              type="text"
              name="lastName"
              value={formData.lastName}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>ID Number</label>
            <input
              type="text"
              name="idNum"
              value={formData.idNum}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Phone Number</label>
            <input
              type="text"
              name="phoneNumber"
              value={formData.phoneNumber}
              onChange={handleInputChange}
              required
            />
          </div>
          <button type="submit">Submit</button>
        </form>
      )}
    </div>
  );
};

export default BookingCalendar;
