import moment from "moment";
import React, { useEffect, useState } from "react";
import { Calendar, momentLocalizer } from "react-big-calendar";
import "react-big-calendar/lib/css/react-big-calendar.css";
import { API_URL } from "../constans";
import "../styles/BookingCalendar.css";

const localizer = momentLocalizer(moment);

const BookingCalendar = () => {
  const [events, setEvents] = useState([]);
  const [selectedDate, setSelectedDate] = useState(null);
  const [selectedTimeSlot, setSelectedTimeSlot] = useState(null);
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    idNum: "",
    phoneNumber: "",
    email: "", // Added email field
    num_of_people: 0,
    totalPrice: 0,
    priceRemaining: 0,
    paid: 0,
  });
  const [exclusiveMeals, setExclusiveMeals] = useState([]);
  const [regularMeals, setRegularMeals] = useState([]);
  const [selectedMeal, setSelectedMeal] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [bookingStatus, setBookingStatus] = useState([]);
  const [edit, setEdit] = useState(false);
  const [deleteMode, setDeleteMode] = useState(false);
  const [pricePerEvent, setPricePerEvent] = useState(0); // Initialize with 0
  const [slotDetails, setSlotDetails] = useState(null); // State to store booking details

  const category = localStorage.getItem("categoryName");

  useEffect(() => {
    const userId = localStorage.getItem("userId");
    if (userId) {
      fetch(`${API_URL}/api/bookings/business/${userId}`)
        .then((response) => response.json())
        .then((data) => {
          const formattedEvents = data.map((event) => ({
            ...event,
            start: new Date(event.date),
            end: new Date(event.date),
            title: `${event.firstName} ${event.lastName}`,
          }));
          setEvents(formattedEvents);
        })
        .catch((error) => console.error("Error fetching events:", error));

      fetch(`${API_URL}/api/meals/regular/user/${userId}`)
        .then((response) => response.json())
        .then((data) => {
          setRegularMeals(data);
        })
        .catch((error) =>
          console.error("Error fetching regular meals:", error)
        );

      fetch(`${API_URL}/api/meals/exclusive/user/${userId}`)
        .then((response) => response.json())
        .then((data) => {
          setExclusiveMeals(data);
        })
        .catch((error) =>
          console.error("Error fetching exclusive meals:", error)
        );

      // Fetch price_per_event
      fetch(`${API_URL}/api/bookings/business/${userId}/capacity`)
        .then((response) => response.json())
        .then((data) => {
          setPricePerEvent(data.price_per_event);
        })
        .catch((error) =>
          console.error("Error fetching price per event:", error)
        );
    }
  }, []);

  const handleSelectSlot = ({ start }) => {
    setSelectedDate(start);
    setSelectedTimeSlot(null); // Reset the time slot when a new date is selected
    setFormData({
      firstName: "",
      lastName: "",
      idNum: "",
      phoneNumber: "",
      email: "",
      num_of_people: 0,
      totalPrice: 0,
      priceRemaining: 0,
      paid: 0,
    });
    fetch(`${API_URL}/api/bookings/check/${start.toISOString().split("T")[0]}`)
      .then((response) => response.json())
      .then((data) => {
        setBookingStatus(data);
        setShowModal(true);
      })
      .catch((error) => console.error("Error checking booking status:", error));
  };

  const handleTimeSelection = (time) => {
    setSelectedTimeSlot(time);
    setEdit(false);
    setDeleteMode(false);
  };

  const handleMealSelection = (meal) => {
    if (selectedMeal?.id === meal.id) {
      // If the selected meal is already selected, unselect it
      setSelectedMeal(null);
    } else {
      // Otherwise, select the meal
      setSelectedMeal(meal);
    }
  };

  const handleBooking = () => {
    const numOfBusiness = localStorage.getItem("userId");
    if (!numOfBusiness) {
      alert("Business ID not found.");
      return;
    }

    if (
      !formData.firstName ||
      !formData.lastName ||
      !formData.idNum ||
      !formData.phoneNumber ||
      !formData.email ||
      !selectedTimeSlot ||
      !selectedMeal
    ) {
      alert(
        "Please fill in all the form fields, select a time slot, and pick a meal."
      );
      return;
    }

    const bookingData = {
      business_id: numOfBusiness,
      date: selectedDate.toISOString().split("T")[0],
      time_slot: selectedTimeSlot,
      ...formData,
      meal_name: selectedMeal.name,
      meal_price: selectedMeal.price,
      meal_photo: selectedMeal.image, // Ensure this is being passed
      total: formData.totalPrice,
      price_remaining: formData.priceRemaining,
      paid: formData.paid,
    };

    fetch(`${API_URL}/api/bookings`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(bookingData),
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.message === "Booking created successfully") {
          setEvents((prevEvents) => [
            ...prevEvents,
            {
              ...formData,
              start: new Date(selectedDate),
              end: new Date(selectedDate),
              title: `${formData.firstName} ${formData.lastName}`,
              time_slot: selectedTimeSlot,
            },
          ]);
          closeForm();
        } else {
          alert(data.message);
        }
      })
      .catch((error) => console.error("Error storing date selection:", error));
  };

  const handleDelete = (timeSlot) => {
    if (
      window.confirm(
        `Are you sure you want to delete the booking for ${timeSlot} slot on ${moment(
          selectedDate
        ).format("MMMM Do YYYY")}?`
      )
    ) {
      const selectedDateString = selectedDate.toISOString().split("T")[0];
      fetch(`${API_URL}/api/bookings/${selectedDateString}/${timeSlot}`, {
        method: "DELETE",
      })
        .then((response) => response.json())
        .then((data) => {
          if (data.message === "Booking deleted successfully") {
            setEvents((prevEvents) =>
              prevEvents.filter(
                (event) =>
                  !(
                    event.date === selectedDateString &&
                    event.time_slot === timeSlot
                  )
              )
            );
            closeForm();
          } else {
            alert(data.message);
          }
        })
        .catch((error) => console.error("Error deleting booking:", error));
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => {
      const updatedFormData = { ...prevData, [name]: value };

      if (
        name === "totalPrice" ||
        name === "paid" ||
        name === "num_of_people"
      ) {
        // Calculate total price when num_of_people, totalPrice, or paid changes
        if (name === "num_of_people" && selectedMeal) {
          const newTotalPrice =
            selectedMeal.price * updatedFormData.num_of_people +
            parseFloat(pricePerEvent); // Add pricePerEvent to total
          updatedFormData.totalPrice = newTotalPrice;
          updatedFormData.priceRemaining = newTotalPrice - updatedFormData.paid;
        } else {
          const priceRemaining =
            updatedFormData.totalPrice - updatedFormData.paid;
          updatedFormData.priceRemaining =
            priceRemaining > 0 ? priceRemaining : 0;
        }
      }

      return updatedFormData;
    });
  };

  const closeForm = () => {
    setSelectedDate(null);
    setSelectedTimeSlot(null);
    setFormData({
      firstName: "",
      lastName: "",
      idNum: "",
      phoneNumber: "",
      email: "",
      num_of_people: 0,
      totalPrice: 0,
      priceRemaining: 0,
      paid: 0,
    });
    setSelectedMeal(null);
    setShowModal(false);
  };
  const handleShowDetails = (timeSlot) => {
  const selectedDateString = selectedDate.toISOString().split("T")[0];
  fetch(`${API_URL}/api/bookings/${selectedDateString}/${timeSlot}/details`)
    .then((response) => response.json())
    .then((data) => {
      setSlotDetails(data); // Store the fetched details
      setShowModal(true); // Show the modal with details
    })
    .catch((error) => console.error("Error fetching slot details:", error));
};

  const renderTimeSlotButtons = () => {
    const dayBooking = bookingStatus.some(
      (booking) => booking.time_slot === "day"
    );
    const nightBooking = bookingStatus.some(
      (booking) => booking.time_slot === "night"
    );

    console.log("Booking Status:", bookingStatus);
    console.log("Day Booking:", dayBooking);
    console.log("Night Booking:", nightBooking);

    return (
      <div>
        {showModal && (
          <div className='modal'>
            <div className='modal-content'>
              <h3>{`Booking Details for ${moment(selectedDate).format("MMMM Do YYYY")}`}</h3>
              <div className='form-content'>
                <h3>Details</h3>
                <form>
                  <div className='form-group'>
                    <label>First Name:</label>
                    <input
                      type='text'
                      name='firstName'
                      value={slotDetails?.firstName || ""}
                      readOnly
                    />
                  </div>
                  <div className='form-group'>
                    <label>Last Name:</label>
                    <input
                      type='text'
                      name='lastName'
                      value={slotDetails?.lastName || ""}
                      readOnly
                    />
                  </div>
                  <div className='form-group'>
                    <label>ID Number:</label>
                    <input
                      type='text'
                      name='idNum'
                      value={slotDetails?.idNum || ""}
                      readOnly
                    />
                  </div>
                  <div className='form-group'>
                    <label>Phone Number:</label>
                    <input
                      type='text'
                      name='phoneNumber'
                      value={slotDetails?.phoneNumber || ""}
                      readOnly
                    />
                  </div>
                  <div className='form-group'>
                    <label>Email:</label>
                    <input
                      type='email'
                      name='email'
                      value={slotDetails?.email || ""}
                      readOnly
                    />
                  </div>
                  {slotDetails?.num_of_people && (
                    <>
                      <div className='form-group'>
                        <label>Number of People:</label>
                        <input
                          type='number'
                          name='num_of_people'
                          value={slotDetails.num_of_people}
                          readOnly
                        />
                      </div>
                      {slotDetails?.meal_name && (
                        <div className='meal-selection'>
                          <h3>Meal Details</h3>
                          <div className='form-group'>
                            <label>Meal Name:</label>
                            <input
                              type='text'
                              name='meal_name'
                              value={slotDetails.meal_name}
                              readOnly
                            />
                          </div>
                          <div className='form-group'>
                            <label>Meal Price:</label>
                            <input
                              type='text'
                              name='meal_price'
                              value={`$${slotDetails.meal_price}`}
                              readOnly
                            />
                          </div>
                          <img
                            src={slotDetails.meal_photo}
                            alt={slotDetails.meal_name}
                            className='meal-photo'
                          />
                        </div>
                      )}
                    </>
                  )}
                  <div className='form-group'>
                    <label>Total Price:</label>
                    <input
                      type='number'
                      name='totalPrice'
                      value={slotDetails?.total || 0}
                      readOnly
                    />
                  </div>
                  <div className='form-group'>
                    <label>Paid:</label>
                    <input
                      type='number'
                      name='paid'
                      value={slotDetails?.paid || 0}
                      readOnly
                    />
                  </div>
                  <div className='form-group'>
                    <label>Price Remaining:</label>
                    <input
                      type='number'
                      name='priceRemaining'
                      value={slotDetails?.price_remaining || 0}
                      readOnly
                    />
                  </div>
                  <div className='form-actions'>
                    <button type='button' onClick={closeForm}>
                      Close
                    </button>
                  </div>
                </form>
              </div>
            </div>
          </div>
        )}
      </div>
    )};

  return (
    <div className='booking-calendar-container'>
      <Calendar
        localizer={localizer}
        events={events}
        selectable
        onSelectSlot={handleSelectSlot}
        defaultView='month'
        views={["month", "agenda"]}
        components={{
          agenda: {
            time: () => null,
          },
          toolbar: (props) => (
            <div className='rbc-toolbar'>
              <span className='rbc-btn-group'>
                <button type='button' onClick={() => props.onNavigate("PREV")}>
                  Back
                </button>
                <button type='button' onClick={() => props.onNavigate("TODAY")}>
                  Today
                </button>
                <button type='button' onClick={() => props.onNavigate("NEXT")}>
                  Next
                </button>
              </span>
              <span className='rbc-toolbar-label'>{props.label}</span>
              <span className='rbc-btn-group'>
                <button type='button' onClick={() => props.onView("month")}>
                  Month
                </button>
                <button type='button' onClick={() => props.onView("agenda")}>
                  Events
                </button>
              </span>
            </div>
          ),
        }}
        style={{ height: 500 }}
      />
      {showModal && (
        <div className='modal'>
          <div className='modal-content'>
            <h3>{`Booking for ${moment(selectedDate).format(
              "MMMM Do YYYY"
            )}`}</h3>
            {selectedTimeSlot ? (
              <div className='form-content'>
                <h3>Booking Details</h3>
                <form>
                  <div className='form-group'>
                    <label>First Name:</label>
                    <input
                      type='text'
                      name='firstName'
                      value={formData.firstName}
                      onChange={handleInputChange}
                    />
                  </div>
                  <div className='form-group'>
                    <label>Last Name:</label>
                    <input
                      type='text'
                      name='lastName'
                      value={formData.lastName}
                      onChange={handleInputChange}
                    />
                  </div>
                  <div className='form-group'>
                    <label>ID Number:</label>
                    <input
                      type='text'
                      name='idNum'
                      value={formData.idNum}
                      onChange={handleInputChange}
                    />
                  </div>
                  <div className='form-group'>
                    <label>Phone Number:</label>
                    <input
                      type='text'
                      name='phoneNumber'
                      value={formData.phoneNumber}
                      onChange={handleInputChange}
                    />
                  </div>
                  <div className='form-group'>
                    <label>Email:</label>
                    <input
                      type='email'
                      name='email'
                      value={formData.email}
                      onChange={handleInputChange}
                    />
                  </div>
                  {category === "Hall" && (
                    <>
                      <div className='form-group'>
                        <label>Number of People:</label>
                        <input
                          type='number'
                          name='num_of_people'
                          value={formData.num_of_people}
                          onChange={handleInputChange}
                        />
                      </div>
                      <div className='meal-selection'>
                        <h3>Select a Meal</h3>

                        <h4>Exclusive Meals</h4>
                        <ul>
                          {exclusiveMeals.map((meal) => (
                            <li
                              key={meal.id}
                              onClick={() => handleMealSelection(meal)}
                            >
                              <input
                                type='radio'
                                name='meal'
                                value={meal.id}
                                checked={selectedMeal?.id === meal.id}
                                onChange={() => handleMealSelection(meal)}
                              />
                              {meal.name} - ${meal.price}
                              <img
                                src={meal.image}
                                alt={meal.name}
                                className='meal-photo'
                              />
                            </li>
                          ))}
                        </ul>

                        <h4>Regular Meals</h4>
                        <ul>
                          {regularMeals.map((meal) => (
                            <li
                              key={meal.id}
                              onClick={() => handleMealSelection(meal)}
                            >
                              <input
                                type='radio'
                                name='meal'
                                value={meal.id}
                                checked={selectedMeal?.id === meal.id}
                                onChange={() => handleMealSelection(meal)}
                              />
                              {meal.name} - ${meal.price}
                              <img
                                src={meal.image}
                                alt={meal.name}
                                className='meal-photo'
                              />
                            </li>
                          ))}
                        </ul>
                      </div>
                      <div className='form-group'>
                        <label>Total Price:</label>
                        <input
                          type='number'
                          name='totalPrice'
                          value={formData.totalPrice}
                          onChange={handleInputChange}
                        />
                      </div>
                      <div className='form-group'>
                        <label>Paid:</label>
                        <input
                          type='number'
                          name='paid'
                          value={formData.paid}
                          onChange={handleInputChange}
                        />
                      </div>
                      <div className='form-group'>
                        <label>Price Remaining:</label>
                        <input
                          type='number'
                          name='priceRemaining'
                          value={formData.priceRemaining}
                          readOnly
                        />
                      </div>
                    </>
                  )}
                  <div className='form-actions'>
                    <button type='button' onClick={handleBooking}>
                      {edit ? "Update" : "Book"}
                    </button>
                    <button type='button' onClick={closeForm}>
                      Cancel
                    </button>
                  </div>
                </form>
              </div>
            ) : (
              renderTimeSlotButtons()
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default BookingCalendar;
