import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/styles.css';
import ProfilePhotoUpload from '../components/ProfilePhotoUpload';
import EditProfileForm from '../components/EditProfileForm';
import UserProfileInfo from '../components/UserProfileInfo';
import useProfileData from '../hooks/useProfileData';
import LogoutButton from '../components/LogoutButton';
import { API_URL } from '../constans';

const Profile = () => {
  const userId = localStorage.getItem('userId');
  const { userDetails, formData, setFormData, profilePhoto, setProfilePhoto } = useProfileData(userId, 'regular');
  const [editing, setEditing] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    fetch(`${API_URL}/api/profile/regular/${userId}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(formData)
    })
      .then(response => response.json())
      .then(data => {
        alert(data.message);
        setEditing(false);
        setUserDetails(formData);
      })
      .catch(error => console.error('Error updating user details:', error));
  };

  if (!userId) {
    return <p>Error: No user ID provided</p>;
  }

  if (userDetails.error) {
    return <p>{userDetails.error}</p>;
  }

  return (
    <div className="profile-container">
      <h1>Regular User Profile</h1>
      <div className="profile-photo-section">
        {profilePhoto ? (
          <img src={profilePhoto} alt="Profile" className="profile-photo" />
        ) : (
          <div className="profile-photo-placeholder">No Profile Photo</div>
        )}
        <ProfilePhotoUpload
          userId={userId}
          profilePhoto={profilePhoto}
          setProfilePhoto={setProfilePhoto}
        />
      </div>
      {editing ? (
        <EditProfileForm
          formData={formData}
          setFormData={setFormData}
          setEditing={setEditing}
          handleSubmit={handleSubmit}
        />
      ) : (
        <div>
          <UserProfileInfo userDetails={userDetails} />
          <button onClick={() => setEditing(true)}>Edit</button>
        </div>
      )}
      <LogoutButton />
    </div>
  );
};

export default Profile;
