import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { API_URL } from '../constans';
import '../styles/CategoryUsers.css';

const CategoryUsers = () => {
  const { categoryId } = useParams();
  const [users, setUsers] = useState([]);

  useEffect(() => {
    const fetchUsersByCategory = async () => {
      try {
        const response = await fetch(`${API_URL}/categories/category/${categoryId}`);
        const data = await response.json();
        setUsers(data);
      } catch (error) {
        console.error('Error fetching users:', error);
      }
    };

    fetchUsersByCategory();
  }, [categoryId]);

  if (users.length === 0) {
    return <p>No users available in this category</p>;
  }

  return (
    <div className="category-users">
      <h2>Users in this Category</h2>
      <div className="users-list">
        {users.map(user => (
          <div key={user.id} className="user-card">
            <img src={user.profile_photo || '/default-avatar.png'} alt={`${user.businessname} profile`} />
            <h3>{user.businessname}</h3>
            <p>Email: {user.email}</p>
            <p>Address: {user.address}</p>
            <p>Category: {user.category_name}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CategoryUsers;
