import React from 'react';

const TimeSlotSelection = ({
    category,
    numOfPeople,
    minGuests,
    hallCapacity,
    selectedDate,
    pricePerEvent,
    dayBooking,
    nightBooking,
    onTimeSelection,
    onInputChange
}) => {
    return (
        <div>
            <h3>
                Select Time Slot for {selectedDate}
            </h3>
            {category === "Hall" && (
                <div>
                    <label>Number of People:</label>
                    <input
                        type='number'
                        value={numOfPeople}
                        onChange={onInputChange}
                        min={minGuests || 1}
                        max={hallCapacity || 1}
                        required
                    />
                </div>
            )}
            {!dayBooking && (
                <button onClick={() => onTimeSelection("day")}>
                    Book Day Slot
                </button>
            )}
            {!nightBooking && (
                <button onClick={() => onTimeSelection("night")}>
                    Book Night Slot
                </button>
            )}
            <h4>Price per Event: ${pricePerEvent}</h4>
            <button onClick={onInputChange}>Cancel</button>
        </div>
    );
};

export default TimeSlotSelection;
