// src/pages/CategoryUsers.js
import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { API_URL } from '../constans';
import '../styles/CategoryUsers.css';

const CategoryUsers = () => {
  const [users, setUsers] = useState([]);
  const { categoryId } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    fetch(`${API_URL}/api/categories/${categoryId}/users`)
      .then(response => response.json())
      .then(data => setUsers(data))
      .catch(error => console.error('Error fetching users:', error));
  }, [categoryId]);

  const handleUserClick = (userId) => {
    navigate(`/user/${userId}`);
  };

  return (
    <div className="category-users-container">
      <h1>Users in this Category</h1>
      <div className="users-grid">
        {users.map(user => (
          <div key={user.id} className="user-item" onClick={() => handleUserClick(user.id)}>
            <img src={user.profilePhoto} alt={user.businessname} />
            <p>{user.businessname}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CategoryUsers;
