import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { API_URL } from '../constans';
import ProfilePhotoSection from '../components/ProfilePhotoSection';
import ProfileDetailsForm from '../components/ProfileDetailsForm';
import ProfileDetailsDisplay from '../components/ProfileDetailsDisplay';
import {
  handleInputChange,
  handleProfilePhotoChange,
  handleProfilePhotoUpload,
  handleSubmit,
  handleLogout
} from '../components/Handlers';
import '../styles/styles.css';

const Profile = () => {
  const [userDetails, setUserDetails] = useState({});
  const [editing, setEditing] = useState(false);
  const [formData, setFormData] = useState({});
  const [profilePhoto, setProfilePhoto] = useState(null);
  const [newProfilePhoto, setNewProfilePhoto] = useState(null);
  const navigate = useNavigate();

  const userId = localStorage.getItem('userId');

  useEffect(() => {
    if (!userId) {
      console.error('No user ID provided');
      return;
    }
    console.log(`Fetching profile for regular user with ID: ${userId}`);
    fetch(`${API_URL}/api/profile/regular/${userId}`)
      .then(response => {
        console.log('Response status:', response.status);
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        console.log('Fetched user details:', data);
        setUserDetails(data);
        setFormData(data);
        setProfilePhoto(data.profilePhoto);
      })
      .catch(error => {
        console.error('Error fetching user details:', error);
        setUserDetails({ error: 'Error fetching user details' });
      });
  }, [userId]);

  if (!userId) {
    return <p>Error: No user ID provided</p>;
  }

  if (userDetails.error) {
    return <p>{userDetails.error}</p>;
  }

  return (
    <div className="profile-container">
      <h1>Regular User Profile</h1>
      <ProfilePhotoSection
        profilePhoto={profilePhoto}
        handleProfilePhotoChange={(e) => handleProfilePhotoChange(e, setNewProfilePhoto)}
        handleProfilePhotoUpload={(e) => handleProfilePhotoUpload(e, userId, newProfilePhoto, setProfilePhoto, setNewProfilePhoto, API_URL)}
      />
      {editing ? (
        <ProfileDetailsForm
          formData={formData}
          handleInputChange={(e) => handleInputChange(e, setFormData)}
          handleSubmit={(e) => handleSubmit(e, userId, formData, setEditing, setUserDetails, API_URL)}
        />
      ) : (
        <ProfileDetailsDisplay
          userDetails={userDetails}
          setEditing={setEditing}
        />
      )}
      <button onClick={() => handleLogout(navigate)} className="logout-button">Logout</button>
    </div>
  );
};

export default Profile;
