import React, { useState } from 'react';
import '../styles/styles.css';
import ProfilePhotoUpload from '../components/ProfilePhotoUpload';
import BusinessEditProfileForm from '../components/BusinessEditProfileForm';
import PhotoGallery from '../components/PhotoGallery';
import BusinessUserProfileInfo from '../components/BusinessUserProfileInfo';
import useProfileData from '../hooks/useProfileData';
import LogoutButton from '../components/LogoutButton';
import { API_URL } from '../constans';

const BusinessProfile = () => {
    const userId = localStorage.getItem('userId');
    const { userDetails, setUserDetails, formData, setFormData, profilePhoto, setProfilePhoto, photos, setPhotos } = useProfileData(userId, 'business');
    const [editing, setEditing] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    fetch(`${API_URL}/api/profile/business/${userId}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(formData)
    })
      .then(response => response.json())
      .then(data => {
        alert(data.message);
        setEditing(false);
        setUserDetails(formData);
      })
      .catch(error => console.error('Error updating user details:', error));
  };

  if (!userId) {
    return <p>Error: No user ID provided</p>;
  }

  if (userDetails.error) {
    return <p>{userDetails.error}</p>;
  }

  return (
    <div className="profile-container">
      <h1>Business Profile</h1>
      {profilePhoto && <img src={profilePhoto} alt="Profile" className="profile-photo" />}
      <ProfilePhotoUpload
        userId={userId}
        profilePhoto={profilePhoto}
        setProfilePhoto={setProfilePhoto}
      />
      <BusinessUserProfileInfo userDetails={userDetails} />
      {editing ? (
        <BusinessEditProfileForm
          formData={formData}
          setFormData={setFormData}
          setEditing={setEditing}
          handleSubmit={handleSubmit}
        />
      ) : (
        <div>
          <button onClick={() => setEditing(true)}>Edit</button>
        </div>
      )}
      <PhotoGallery photos={photos} userId={userId} setPhotos={setPhotos} />
      <LogoutButton />
    </div>
  );
};

export default BusinessProfile;
