import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { API_URL } from '../constans';
import '../styles/s.css'; // Ensure you have a corresponding CSS file for styling

const RegularUserRegister = () => {
  const [formData, setFormData] = useState({
    firstname: '',
    lastname: '',
    id: '',
    number: '',
    address: '',
    dob: '',
    email: '',
    password: '',
  });
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    try {
      const response = await fetch(`${API_URL}/api/register/regular`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });

      const data = await response.json();
      if (response.ok) {
        alert(data.message);
        navigate('/login'); // Redirect to login after successful registration
      } else {
        setError(data.message || 'Error registering regular user');
      }
    } catch (error) {
      console.error('Error registering regular user:', error);
      setError('Error registering regular user. Please try again.');
    }
  };

  return (
    <div className="register-container">
      <form onSubmit={handleSubmit}>
        <h2>Register Regular User</h2>
        {Object.keys(formData).map((key) => (
          <div key={key} className="form-group">
            <label>{key.charAt(0).toUpperCase() + key.slice(1)}:</label>
            <input
              type={key === 'dob' ? 'date' : key === 'email' ? 'email' : key === 'password' ? 'password' : 'text'}
              name={key}
              value={formData[key]}
              onChange={handleInputChange}
            />
          </div>
        ))}
        <button type="submit">Register</button>
        {error && <p className="error">{error}</p>}
      </form>
    </div>
  );
};

export default RegularUserRegister;
