import React, { useState, useEffect } from "react";
import moment from "moment";
import { Calendar, momentLocalizer } from "react-big-calendar";
import "react-big-calendar/lib/css/react-big-calendar.css";
import { API_URL } from "../constans";
import "../styles/BookingCalendar.css";

const localizer = momentLocalizer(moment);

const BookingCalendar = () => {
  const [events, setEvents] = useState([]); // Events in the calendar
  const [selectedDate, setSelectedDate] = useState(null); // Date selected in the calendar
  const [selectedTimeSlot, setSelectedTimeSlot] = useState(null); // Time slot selected
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    idNum: "",
    phoneNumber: "",
    email: "", // Added email field
    num_of_people: 0,
    totalPrice: 0,
    priceRemaining: 0,
    paid: 0,
  }); // Form data for booking
  const [exclusiveMeals, setExclusiveMeals] = useState([]); // Exclusive meals for the business
  const [regularMeals, setRegularMeals] = useState([]); // Regular meals for the business
  const [selectedMeal, setSelectedMeal] = useState(null); // Currently selected meal
  const [showModal, setShowModal] = useState(false); // Controls visibility of the modal
  const [bookingStatus, setBookingStatus] = useState([]); // Status of the bookings on the selected date
  const [editMode, setEditMode] = useState(false); // Tracks if we are editing a booking
  const [selectedBooking, setSelectedBooking] = useState(null); // Store the selected booking
  const [pricePerEvent, setPricePerEvent] = useState(0); // Price per event

  const category = localStorage.getItem("categoryName");

  useEffect(() => {
    const userId = localStorage.getItem("userId");
    if (userId) {
      fetch(`${API_URL}/api/bookings/business/${userId}`)
        .then((response) => response.json())
        .then((data) => {
          const formattedEvents = data.map((event) => ({
            ...event,
            start: new Date(event.date),
            end: new Date(event.date),
            title: `${event.firstName} ${event.lastName}`,
          }));
          setEvents(formattedEvents);
        })
        .catch((error) => console.error("Error fetching events:", error));

      fetch(`${API_URL}/api/meals/regular/user/${userId}`)
        .then((response) => response.json())
        .then((data) => {
          setRegularMeals(data);
        })
        .catch((error) =>
          console.error("Error fetching regular meals:", error)
        );

      fetch(`${API_URL}/api/meals/exclusive/user/${userId}`)
        .then((response) => response.json())
        .then((data) => {
          setExclusiveMeals(data);
        })
        .catch((error) =>
          console.error("Error fetching exclusive meals:", error)
        );

      fetch(`${API_URL}/api/bookings/business/${userId}/capacity`)
        .then((response) => response.json())
        .then((data) => {
          setPricePerEvent(data.price_per_event);
        })
        .catch((error) => console.error("Error fetching price per event:", error));
    }
  }, []);

  const handleSelectSlot = ({ start }) => {
    setSelectedDate(start);
    setSelectedTimeSlot(null);
    setEditMode(false);
    setSelectedBooking(null); // Reset selected booking
    setFormData({
      firstName: "",
      lastName: "",
      idNum: "",
      phoneNumber: "",
      email: "",
      num_of_people: 0,
      totalPrice: 0,
      priceRemaining: 0,
      paid: 0,
    });
    fetch(`${API_URL}/api/bookings/check/${start.toISOString().split("T")[0]}`)
      .then((response) => response.json())
      .then((data) => {
        setBookingStatus(data);
        setShowModal(true);
      })
      .catch((error) => console.error("Error checking booking status:", error));
  };

  const handleBooking = () => {
    const url = editMode
      ? `${API_URL}/api/bookings/${selectedBooking.id}`
      : `${API_URL}/api/bookings`;

    const method = editMode ? "PUT" : "POST";

    const bookingData = {
      business_id: localStorage.getItem("userId"),
      date: selectedDate.toISOString().split("T")[0],
      time_slot: selectedTimeSlot,
      ...formData,
      meal_name: selectedMeal?.name,
      meal_price: selectedMeal?.price,
      meal_photo: selectedMeal?.image,
      total: formData.totalPrice,
      price_remaining: formData.priceRemaining,
      paid: formData.paid,
    };

    fetch(url, {
      method,
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(bookingData),
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.message === "Booking updated successfully" || data.message === "Booking created successfully") {
          // If in edit mode, update the existing event
          if (editMode) {
            setEvents((prevEvents) =>
              prevEvents.map((event) =>
                event.id === selectedBooking.id
                  ? {
                      ...event,
                      ...bookingData,
                      start: new Date(selectedDate),
                      end: new Date(selectedDate),
                      title: `${formData.firstName} ${formData.lastName}`,
                    }
                  : event
              )
            );
          } else {
            // If not in edit mode, add the new event
            setEvents((prevEvents) => [
              ...prevEvents,
              {
                ...formData,
                start: new Date(selectedDate),
                end: new Date(selectedDate),
                title: `${formData.firstName} ${formData.lastName}`,
                time_slot: selectedTimeSlot,
              },
            ]);
          }
          closeForm();
        } else {
          alert(data.message);
        }
      })
      .catch((error) => console.error("Error storing date selection:", error));
  };

  const handleMealSelection = (meal) => {
    setSelectedMeal(meal);
  };

  const handleEditBooking = (booking) => {
    setEditMode(true);
    setSelectedBooking(booking);
    setSelectedDate(new Date(booking.date));
    setSelectedTimeSlot(booking.time_slot);
    setFormData({
      firstName: booking.firstName,
      lastName: booking.lastName,
      idNum: booking.idNum,
      phoneNumber: booking.phoneNumber,
      email: booking.email,
      num_of_people: booking.num_of_people,
      totalPrice: booking.total,
      priceRemaining: booking.price_remaining,
      paid: booking.paid,
    });
    setSelectedMeal({
      name: booking.meal_name,
      price: booking.meal_price,
      image: booking.meal_photo,
    });
    setShowModal(true);
  };

  const handleDelete = (timeSlot) => {
    if (
      window.confirm(
        `Are you sure you want to delete the booking for ${timeSlot} slot on ${moment(
          selectedDate
        ).format("MMMM Do YYYY")}?`
      )
    ) {
      const selectedDateString = selectedDate.toISOString().split("T")[0];
      fetch(`${API_URL}/api/bookings/${selectedDateString}/${timeSlot}`, {
        method: "DELETE",
      })
        .then((response) => response.json())
        .then((data) => {
          if (data.message === "Booking deleted successfully") {
            setEvents((prevEvents) =>
              prevEvents.filter(
                (event) =>
                  !(
                    event.date === selectedDateString &&
                    event.time_slot === timeSlot
                  )
              )
            );
            closeForm();
          } else {
            alert(data.message);
          }
        })
        .catch((error) => console.error("Error deleting booking:", error));
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => {
      const updatedFormData = { ...prevData, [name]: value };

      if (
        name === "totalPrice" ||
        name === "paid" ||
        name === "num_of_people"
      ) {
        if (name === "num_of_people" && selectedMeal) {
          const newTotalPrice =
            selectedMeal.price * updatedFormData.num_of_people +
            parseFloat(pricePerEvent);
          updatedFormData.totalPrice = newTotalPrice;
          updatedFormData.priceRemaining = newTotalPrice - updatedFormData.paid;
        } else {
          const priceRemaining =
            updatedFormData.totalPrice - updatedFormData.paid;
          updatedFormData.priceRemaining =
            priceRemaining > 0 ? priceRemaining : 0;
        }
      }

      return updatedFormData;
    });
  };

  const closeForm = () => {
    setSelectedDate(null);
    setSelectedTimeSlot(null);
    setFormData({
      firstName: "",
      lastName: "",
      idNum: "",
      phoneNumber: "",
      email: "",
      num_of_people: 0,
      totalPrice: 0,
      priceRemaining: 0,
      paid: 0,
    });
    setSelectedMeal(null);
    setShowModal(false);
    setEditMode(false);
    setSelectedBooking(null);
  };

  const renderTimeSlotButtons = () => {
    const dayBooking = bookingStatus.some(
      (booking) => booking.time_slot === "day"
    );
    const nightBooking = bookingStatus.some(
      (booking) => booking.time_slot === "night"
    );

    return (
      <div className='time-slot-buttons'>
        {!dayBooking && (
          <button onClick={() => handleTimeSelection("day")}>
            Book Day Slot
          </button>
        )}
        {dayBooking && (
          <>
            <button onClick={() => handleDelete("day")}>Delete Day Slot</button>
            <button onClick={() => handleEditBooking({ time_slot: "day" })}>
              Update Day Slot
            </button>
          </>
        )}
        {!nightBooking && (
          <button onClick={() => handleTimeSelection("night")}>
            Book Night Slot
          </button>
        )}
        {nightBooking && (
          <>
            <button onClick={() => handleDelete("night")}>
              Delete Night Slot
            </button>
            <button onClick={() => handleEditBooking({ time_slot: "night" })}>
              Update Night Slot
            </button>
          </>
        )}
        <button onClick={closeForm}>Cancel</button>
      </div>
    );
  };

  return (
    <div className='booking-calendar-container'>
      <Calendar
        localizer={localizer}
        events={events}
        selectable
        onSelectSlot={handleSelectSlot}
        defaultView='month'
        views={["month", "agenda"]}
        components={{
          agenda: {
            time: () => null,
          },
          toolbar: (props) => (
            <div className='rbc-toolbar'>
              <span className='rbc-btn-group'>
                <button type='button' onClick={() => props.onNavigate("PREV")}>
                  Back
                </button>
                <button type='button' onClick={() => props.onNavigate("TODAY")}>
                  Today
                </button>
                <button type='button' onClick={() => props.onNavigate("NEXT")}>
                  Next
                </button>
              </span>
              <span className='rbc-toolbar-label'>{props.label}</span>
              <span className='rbc-btn-group'>
                <button type='button' onClick={() => props.onView("month")}>
                  Month
                </button>
                <button type='button' onClick={() => props.onView("agenda")}>
                  Events
                </button>
              </span>
            </div>
          ),
        }}
        style={{ height: 500 }}
      />
      {showModal && (
        <div className='modal'>
          <div className='modal-content'>
            <h3>{`Booking for ${moment(selectedDate).format(
              "MMMM Do YYYY"
            )}`}</h3>
            {selectedTimeSlot ? (
              <div className='form-content'>
                <h3>Booking Details</h3>
                <form>
                  <div className='form-group'>
                    <label>First Name:</label>
                    <input
                      type='text'
                      name='firstName'
                      value={formData.firstName}
                      onChange={handleInputChange}
                    />
                  </div>
                  <div className='form-group'>
                    <label>Last Name:</label>
                    <input
                      type='text'
                      name='lastName'
                      value={formData.lastName}
                      onChange={handleInputChange}
                    />
                  </div>
                  <div className='form-group'>
                    <label>ID Number:</label>
                    <input
                      type='text'
                      name='idNum'
                      value={formData.idNum}
                      onChange={handleInputChange}
                    />
                  </div>
                  <div className='form-group'>
                    <label>Phone Number:</label>
                    <input
                      type='text'
                      name='phoneNumber'
                      value={formData.phoneNumber}
                      onChange={handleInputChange}
                    />
                  </div>
                  <div className='form-group'>
                    <label>Email:</label>
                    <input
                      type='email'
                      name='email'
                      value={formData.email}
                      onChange={handleInputChange}
                    />
                  </div>
                  {category === "Hall" && (
                    <>
                      <div className='form-group'>
                        <label>Number of People:</label>
                        <input
                          type='number'
                          name='num_of_people'
                          value={formData.num_of_people}
                          onChange={handleInputChange}
                        />
                      </div>
                      <div className='meal-selection'>
                        <h3>Select a Meal</h3>

                        <h4>Exclusive Meals</h4>
                        <ul>
                          {exclusiveMeals.map((meal) => (
                            <li
                              key={meal.id}
                              onClick={() => handleMealSelection(meal)}
                            >
                              <input
                                type='radio'
                                name='meal'
                                value={meal.id}
                                checked={selectedMeal?.id === meal.id}
                                onChange={() => handleMealSelection(meal)}
                              />
                              {meal.name} - ${meal.price}
                              <img
                                src={meal.image}
                                alt={meal.name}
                                className='meal-photo'
                              />
                            </li>
                          ))}
                        </ul>

                        <h4>Regular Meals</h4>
                        <ul>
                          {regularMeals.map((meal) => (
                            <li
                              key={meal.id}
                              onClick={() => handleMealSelection(meal)}
                            >
                              <input
                                type='radio'
                                name='meal'
                                value={meal.id}
                                checked={selectedMeal?.id === meal.id}
                                onChange={() => handleMealSelection(meal)}
                              />
                              {meal.name} - ${meal.price}
                              <img
                                src={meal.image}
                                alt={meal.name}
                                className='meal-photo'
                              />
                            </li>
                          ))}
                        </ul>
                      </div>
                      <div className='form-group'>
                        <label>Total Price:</label>
                        <input
                          type='number'
                          name='totalPrice'
                          value={formData.totalPrice}
                          onChange={handleInputChange}
                        />
                      </div>
                      <div className='form-group'>
                        <label>Paid:</label>
                        <input
                          type='number'
                          name='paid'
                          value={formData.paid}
                          onChange={handleInputChange}
                        />
                      </div>
                      <div className='form-group'>
                        <label>Price Remaining:</label>
                        <input
                          type='number'
                          name='priceRemaining'
                          value={formData.priceRemaining}
                          readOnly
                        />
                      </div>
                    </>
                  )}
                  <div className='form-actions'>
                    <button type='button' onClick={handleBooking}>
                      {editMode ? "Update" : "Book"}
                    </button>
                    <button type='button' onClick={closeForm}>
                      Cancel
                    </button>
                  </div>
                </form>
              </div>
            ) : (
              renderTimeSlotButtons()
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default BookingCalendar;
