import moment from "moment";
import React, { useEffect, useState } from "react";
import { Calendar, momentLocalizer } from "react-big-calendar";
import "react-big-calendar/lib/css/react-big-calendar.css";
import { API_URL } from "../constants";
import "../styles/BookingCalendar.css";

const localizer = momentLocalizer(moment);

const BookingCalendar = () => {
  const [events, setEvents] = useState([]);
  const [selectedDate, setSelectedDate] = useState(null);
  const [selectedTimeSlot, setSelectedTimeSlot] = useState(null);
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    idNum: "",
    phoneNumber: "",
    email: "",
    num_of_people: 0,
    totalPrice: 0,
    priceRemaining: 0,
    paid: 0,
  });
  const [selectedMeal, setSelectedMeal] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [showDetails, setShowDetails] = useState(false);
  const [editMode, setEditMode] = useState(false);

  // Fetch events on mount
  useEffect(() => {
    const userId = localStorage.getItem("userId");
    if (userId) {
      fetchEvents(userId);
    }
  }, []);

  const fetchEvents = (userId) => {
    fetch(`${API_URL}/api/bookings/business/${userId}`)
      .then((response) => response.json())
      .then((data) => {
        const formattedEvents = data.map((event) => ({
          ...event,
          start: new Date(event.date),
          end: new Date(event.date),
          title: `${event.firstName} ${event.lastName}`,
        }));
        setEvents(formattedEvents);
      })
      .catch((error) => console.error("Error fetching events:", error));
  };

  const handleSelectSlot = ({ start }) => {
    setSelectedDate(start);
    setSelectedTimeSlot(null);
    setShowModal(true);
    resetFormData();
  };

  const handleTimeSelection = (time) => {
    setSelectedTimeSlot(time);
    setEditMode(false);
    setShowDetails(false);
  };

  const resetFormData = () => {
    setFormData({
      firstName: "",
      lastName: "",
      idNum: "",
      phoneNumber: "",
      email: "",
      num_of_people: 0,
      totalPrice: 0,
      priceRemaining: 0,
      paid: 0,
    });
    setSelectedMeal(null);
  };

  const handleBooking = () => {
    const userId = localStorage.getItem("userId");

    if (!formData.firstName || !selectedTimeSlot || !selectedMeal) {
      alert("Please fill in all the required fields.");
      return;
    }

    const bookingData = {
      business_id: userId,
      date: selectedDate.toISOString().split("T")[0],
      time_slot: selectedTimeSlot,
      ...formData,
      meal_name: selectedMeal.name,
      meal_price: selectedMeal.price,
      meal_photo: selectedMeal.image,
    };

    fetch(`${API_URL}/api/bookings`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(bookingData),
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.message === "Booking created successfully") {
          fetchEvents(userId);
          setShowModal(false);
        } else {
          alert(data.message);
        }
      })
      .catch((error) => console.error("Error creating booking:", error));
  };

  const handleShowDetails = () => {
    const userId = localStorage.getItem("userId");

    if (!selectedTimeSlot) {
      console.error("No time slot selected");
      return;
    }

    fetch(
      `${API_URL}/api/bookings/details?date=${
        selectedDate.toISOString().split("T")[0]
      }&time_slot=${selectedTimeSlot}&business_id=${userId}`
    )
      .then((response) => response.json())
      .then((data) => {
        if (data.message !== "Booking not found") {
          setFormData({
            firstName: data.firstName,
            lastName: data.lastName,
            idNum: data.idNum,
            phoneNumber: data.phoneNumber,
            email: data.email,
            num_of_people: data.num_of_people,
            totalPrice: data.total_price,
            priceRemaining: data.price_remaining,
            paid: data.paid,
          });
          setSelectedMeal({
            name: data.meal_name,
            price: data.meal_price,
            image: data.meal_photo,
          });
          setShowDetails(true);
          setEditMode(true);
        } else {
          alert(data.message);
        }
      })
      .catch((error) => console.error("Error fetching booking details:", error));
  };

  const handleUpdateBooking = () => {
    const userId = localStorage.getItem("userId");

    const bookingData = {
      business_id: userId,
      date: selectedDate.toISOString().split("T")[0],
      time_slot: selectedTimeSlot,
      ...formData,
      meal_name: selectedMeal.name,
      meal_price: selectedMeal.price,
      meal_photo: selectedMeal.image,
    };

    fetch(`${API_URL}/api/bookings/update`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(bookingData),
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.message === "Booking updated successfully") {
          fetchEvents(userId);
          setShowModal(false);
        } else {
          alert(data.message);
        }
      })
      .catch((error) => console.error("Error updating booking:", error));
  };

  const handleDeleteBooking = () => {
    const userId = localStorage.getItem("userId");

    if (!selectedTimeSlot) {
      alert("No time slot selected for deletion.");
      return;
    }

    fetch(
      `${API_URL}/api/bookings/${selectedDate.toISOString().split("T")[0]}/${selectedTimeSlot}`,
      {
        method: "DELETE",
      }
    )
      .then((response) => response.json())
      .then((data) => {
        if (data.message === "Booking deleted successfully") {
          fetchEvents(userId);
          setShowModal(false);
        } else {
          alert(data.message);
        }
      })
      .catch((error) => console.error("Error deleting booking:", error));
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const renderTimeSlotButtons = () => {
    return (
      <div className="time-slot-buttons">
        <button onClick={() => handleTimeSelection("day")}>Day Slot</button>
        <button onClick={() => handleTimeSelection("night")}>Night Slot</button>
        <button onClick={closeForm}>Cancel</button>
      </div>
    );
  };

  const renderDetailsForm = () => (
    <div className="details-form">
      <h3>Booking Details</h3>
      <div className="form-group">
        <label>First Name:</label>
        <input
          type="text"
          name="firstName"
          value={formData.firstName}
          onChange={handleInputChange}
        />
      </div>
      <div className="form-group">
        <label>Last Name:</label>
        <input
          type="text"
          name="lastName"
          value={formData.lastName}
          onChange={handleInputChange}
        />
      </div>
      <div className="form-group">
        <label>ID Number:</label>
        <input
          type="text"
          name="idNum"
          value={formData.idNum}
          onChange={handleInputChange}
        />
      </div>
      <div className="form-group">
        <label>Phone Number:</label>
        <input
          type="text"
          name="phoneNumber"
          value={formData.phoneNumber}
          onChange={handleInputChange}
        />
      </div>
      <div className="form-group">
        <label>Email:</label>
        <input
          type="email"
          name="email"
          value={formData.email}
          onChange={handleInputChange}
        />
      </div>
      <div className="form-group">
        <label>Number of People:</label>
        <input
          type="number"
          name="num_of_people"
          value={formData.num_of_people}
          onChange={handleInputChange}
        />
      </div>
      <div className="form-group">
        <label>Total Price:</label>
        <input
          type="number"
          name="totalPrice"
          value={formData.totalPrice}
          onChange={handleInputChange}
        />
      </div>
      <div className="form-group">
        <label>Paid:</label>
        <input
          type="number"
          name="paid"
          value={formData.paid}
          onChange={handleInputChange}
        />
      </div>
      <div className="form-group">
        <label>Price Remaining:</label>
        <input
          type="number"
          name="priceRemaining"
          value={formData.priceRemaining}
          readOnly
        />
      </div>
      <div className="form-actions">
        <button type="button" onClick={closeForm}>
          Close
        </button>
        {editMode ? (
          <button type="button" onClick={handleUpdateBooking}>
            Update Booking
          </button>
        ) : (
          <button type="button" onClick={handleBooking}>
            Book Slot
          </button>
        )}
        {editMode && (
          <button type="button" onClick={handleDeleteBooking}>
            Delete Booking
          </button>
        )}
      </div>
    </div>
  );

  const closeForm = () => {
    setSelectedDate(null);
    setSelectedTimeSlot(null);
    setShowModal(false);
    setShowDetails(false);
    resetFormData();
  };

  return (
    <div className="booking-calendar-container">
      <Calendar
        localizer={localizer}
        events={events}
        selectable
        onSelectSlot={handleSelectSlot}
        defaultView="month"
        views={["month", "agenda"]}
        components={{
          toolbar: (props) => (
            <div className="rbc-toolbar">
              <span className="rbc-btn-group">
                <button type="button" onClick={() => props.onNavigate("PREV")}>
                  Back
                </button>
                <button type="button" onClick={() => props.onNavigate("TODAY")}>
                  Today
                </button>
                <button type="button" onClick={() => props.onNavigate("NEXT")}>
                  Next
                </button>
              </span>
              <span className="rbc-toolbar-label">{props.label}</span>
              <span className="rbc-btn-group">
                <button type="button" onClick={() => props.onView("month")}>
                  Month
                </button>
                <button type="button" onClick={() => props.onView("agenda")}>
                  Agenda
                </button>
              </span>
            </div>
          ),
        }}
        style={{ height: 500 }}
      />
      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <h3>{`Booking for ${moment(selectedDate).format("MMMM Do YYYY")}`}</h3>
            {selectedTimeSlot && showDetails
              ? renderDetailsForm()
              : renderTimeSlotButtons()}
          </div>
        </div>
      )}
    </div>
  );
};

export default BookingCalendar;
