// src/pages/Categories.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { API_URL } from '../constans';
import '../styles/Categories.css';

const Categories = () => {
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [categoryPhoto, setCategoryPhoto] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    fetch(`${API_URL}/api/categories`)
      .then(response => response.json())
      .then(data => {
        console.log('Fetched categories:', data);
        setCategories(data);
      })
      .catch(error => console.error('Error fetching categories:', error));
  }, []);

  const handleCategoryClick = (categoryId) => {
    navigate(`/category/${categoryId}`);
  };

  const handlePhotoChange = (e) => {
    setCategoryPhoto(e.target.files[0]);
  };

  const handlePhotoUpload = (e) => {
    e.preventDefault();
    if (!selectedCategory || !categoryPhoto) return;

    const formData = new FormData();
    formData.append('categoryPhoto', categoryPhoto);

    fetch(`${API_URL}/api/categories/${selectedCategory}/upload-photo`, {
      method: 'POST',
      body: formData,
    })
      .then(response => response.json())
      .then(data => {
        alert(data.message);
        setCategoryPhoto(null);
        setSelectedCategory(null);
        // Optionally refetch categories to update the list
        fetch(`${API_URL}/api/categories`)
          .then(response => response.json())
          .then(data => setCategories(data))
          .catch(error => console.error('Error fetching categories:', error));
      })
      .catch(error => console.error('Error uploading category photo:', error));
  };

  return (
    <div className="categories-container">
      <h1>Categories</h1>
      <div className="categories-grid">
        {categories.length > 0 ? (
          categories.map(category => (
            <div key={category.id} className="category-item" onClick={() => handleCategoryClick(category.id)}>
              <img src={category.category_photo ? `${API_URL}${category.category_photo}` : "https://via.placeholder.com/100"} alt={category.name} />
              <p>{category.name}</p>
            </div>
          ))
        ) : (
          <p>No categories available</p>
        )}
      </div>
      <div className="upload-section">
        <h2>Upload Category Photo</h2>
        <form onSubmit={handlePhotoUpload}>
          <select onChange={(e) => setSelectedCategory(e.target.value)} value={selectedCategory || ''}>
            <option value="">Select Category</option>
            {categories.map(category => (
              <option key={category.id} value={category.id}>
                {category.name}
              </option>
            ))}
          </select>
          <input type="file" onChange={handlePhotoChange} />
          <button type="submit">Upload Photo</button>
        </form>
      </div>
    </div>
  );
};

export default Categories;
