import React, { useContext } from "react";
import {
  Navigate,
  Route,
  BrowserRouter as Router,
  Routes,
} from "react-router-dom";
import BusinessNavbar from "./components/BusinessLayout";
import BusinessUserRegister from "./components/BusinessUserRegister";
import Layout from "./components/Layout";
import Register from "./components/RegularUserRegister";
import AuthContext from "./contexts/AuthContext";
import BusinessProfile from "./pages/BusinessProfile";
import Dates from "./pages/Dates";
import Financial from "./pages/Financial";
import Home from "./pages/Home";
import Login from "./pages/Login";
import Profile from "./pages/Profile";
import Services from "./pages/Services";

const App = () => {
  const { isAuthenticated } = useContext(AuthContext);
  const userType = localStorage.getItem("userType"); // Get userType from localStorage

  return (
    <Router>
      <Routes>
        <Route element={<Layout />}>
          <Route path='/' element={<Home />} />
          <Route
            path='/login'
            element={isAuthenticated ? <Navigate to='/profile' /> : <Login />}
          />
          <Route
            path='/register'
            element={
              isAuthenticated ? <Navigate to='/profile' /> : <Register />
            }
          />
          <Route
            path='/register-business'
            element={
              isAuthenticated ? (
                <Navigate to='/profile' />
              ) : (
                <BusinessUserRegister />
              )
            }
          />
        </Route>
        <Route element={<Layout />}>
          <Route
            path='/profile'
            element={
              isAuthenticated ? (
                userType === "business" ? (
                  <>
                    <BusinessProfile />
                  </>
                ) : (
                  <Profile />
                )
              ) : (
                <Navigate to='/login' />
              )
            }
          />
          <Route
            path='/financial'
            element={
              isAuthenticated ? (
                <>
                  <BusinessNavbar />
                  <Financial />
                </>
              ) : (
                <Navigate to='/login' />
              )
            }
          />
          <Route
            path='/dates'
            element={
              isAuthenticated ? (
                <>
                  <BusinessNavbar />
                  <Dates />
                </>
              ) : (
                <Navigate to='/login' />
              )
            }
          />
          <Route
            path='/services'
            element={
              isAuthenticated ? (
                <>
                  <BusinessNavbar />
                  <Services />
                </>
              ) : (
                <Navigate to='/login' />
              )
            }
          />
        </Route>
      </Routes>
    </Router>
  );
};

export default App;
