import React from "react";

const TimeSlotSelection = ({
  category,
  numOfPeople,
  minGuests,
  hallCapacity,
  dayBooking,
  nightBooking,
  onTimeSelect,
  setNumOfPeople,
  pricePerEvent,
}) => {
  return (
    <div>
      {category === "Hall" && (
        <div>
          <label>Number of People:</label>
          <input
            type="number"
            value={numOfPeople}
            onChange={(e) => setNumOfPeople(e.target.value)}
            min={minGuests || 1}
            max={hallCapacity || 1}
            required
          />
        </div>
      )}
      {!dayBooking && (
        <button onClick={() => onTimeSelect("day")}>Book Day Slot</button>
      )}
      {!nightBooking && (
        <button onClick={() => onTimeSelect("night")}>Book Night Slot</button>
      )}
      <h4>Price per Event: ${pricePerEvent}</h4>
      <button onClick={() => onTimeSelect(null)}>Cancel</button>
    </div>
  );
};

export default TimeSlotSelection;
