import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import CalendarComponent from "./components/CalendarComponent";
import ModalComponent from "./components/ModalComponent";
import MealSelection from "./components/MealSelection";
import TimeSlotSelection from "./components/TimeSlotSelection";
import NavbarBooking from "./NavbarBooking";
import "../styles/Dates.css";

const Dates = () => {
  const { userId } = useParams();
  const [category, setCategory] = useState("");
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedDate, setSelectedDate] = useState(null);
  const [selectedTimeSlot, setSelectedTimeSlot] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [showMealsSelection, setShowMealsSelection] = useState(false);
  const [bookingStatus, setBookingStatus] = useState([]);
  const [userType, setUserType] = useState(null);
  const [numOfPeople, setNumOfPeople] = useState("");
  const [hallCapacity, setHallCapacity] = useState(null);
  const [minGuests, setMinGuests] = useState(null);
  const [exclusiveMeal, setExclusiveMeal] = useState(null);
  const [regularMeals, setRegularMeals] = useState([]);
  const [selectedMeal, setSelectedMeal] = useState(null);
  const [totalPrice, setTotalPrice] = useState(0);
  const [pricePerEvent, setPricePerEvent] = useState(0);
  const [advancePayment, setAdvancePayment] = useState(0);
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    idNum: "",
    phoneNumber: "",
    email: "",
    num_of_people: 0,
    totalPrice: 0,
    priceRemaining: 0,
    paid: 0,
  });

  useEffect(() => {
    // Fetch user type, category, hall details, booked dates, meals, etc.
    // Here the states should be populated from the fetch responses.
  }, [userId]);

  const handleSelectSlot = ({ start }) => {
    // Handle slot selection logic
    setSelectedDate(start);
  };

  const closeForm = () => {
    setSelectedMeal(null);
    setTotalPrice(pricePerEvent);
    setSelectedDate(null);
    setSelectedTimeSlot(null);
    setNumOfPeople(0);
    setFormData({
      firstName: "",
      lastName: "",
      idNum: "",
      phoneNumber: "",
      email: "",
      num_of_people: 0,
      totalPrice: 0,
      priceRemaining: 0,
      paid: 0,
    });
    setShowModal(false);
    setShowMealsSelection(false);
  };

  const handleMealSelection = (meal) => {
    if (selectedMeal && selectedMeal.id === meal.id) {
      setSelectedMeal(null);
      setTotalPrice(pricePerEvent);
      setAdvancePayment(pricePerEvent * 0.05);
    } else {
      setSelectedMeal(meal);
      calculateTotalPrice(meal.price);
    }
  };

  const calculateTotalPrice = (mealPrice = 0) => {
    const total =
      category === "Hall"
        ? mealPrice * numOfPeople + pricePerEvent
        : pricePerEvent;
    setTotalPrice(total);
    setAdvancePayment((total * 0.05).toFixed(2));
  };

  const handleTimeSelection = (time) => {
    setSelectedTimeSlot(time);
    if (category === "Hall") {
      if (
        !numOfPeople ||
        numOfPeople < minGuests ||
        numOfPeople > hallCapacity
      ) {
        alert(
          `The number of people must be between ${minGuests} and ${hallCapacity}.`
        );
        return;
      }
      setShowModal(false);
      setShowMealsSelection(true);
    } else {
      setTotalPrice(pricePerEvent);
      setShowModal(false);
      handleBooking(time);
    }
  };

  const handleBooking = (timeSlot, skipMeal = false) => {
    if (!timeSlot) {
      alert("Please select a time slot before booking.");
      return;
    }

    const businessId = userId;
    const remainingBalance = (totalPrice - advancePayment).toFixed(2);
    let bookingDetails = {
      business_id: businessId,
      user_id: localStorage.getItem("userId"),
      date: selectedDate.toISOString().split("T")[0],
      time_slot: timeSlot,
      num_of_people: category === "Hall" ? numOfPeople : null,
      total_price: totalPrice,
      advance_payment: advancePayment,
      remaining_balance: remainingBalance,
    };

    if (category === "Hall" && !skipMeal) {
      const meal_image = selectedMeal ? `/uploads/${selectedMeal.image}` : null;

      bookingDetails = {
        ...bookingDetails,
        meal_name: selectedMeal?.name || null,
        meal_price: selectedMeal?.price || null,
        meal_image: meal_image || null,
      };
    }

    // Assume fetch call goes here
  };

  if (loading) {
    return <p>Loading...</p>;
  }

  const dayBooking = bookingStatus.some(
    (booking) => booking.time_slot === "day"
  );
  const nightBooking = bookingStatus.some(
    (booking) => booking.time_slot === "night"
  );

  return (
    <div className="dates">
      <NavbarBooking userId={userId} />
      <h2>Booked Dates</h2>
      <CalendarComponent
        events={events}
        localizer={localizer}
        onSelectSlot={handleSelectSlot}
      />
      {showModal && (
        <ModalComponent isOpen={showModal} onClose={closeForm}>
          <TimeSlotSelection
            category={category}
            numOfPeople={numOfPeople}
            minGuests={minGuests}
            hallCapacity={hallCapacity}
            dayBooking={dayBooking}
            nightBooking={nightBooking}
            onTimeSelect={handleTimeSelection}
            setNumOfPeople={setNumOfPeople}
            pricePerEvent={pricePerEvent}
          />
        </ModalComponent>
      )}
      {showMealsSelection && (
        <ModalComponent isOpen={showMealsSelection} onClose={closeForm}>
          <MealSelection
            exclusiveMeal={exclusiveMeal}
            regularMeals={regularMeals}
            selectedMeal={selectedMeal}
            onSelectMeal={handleMealSelection}
          />
          <h4>Total Price: ${totalPrice}</h4>
          <h4>Advance Payment (5%): ${advancePayment}</h4>
          <button onClick={() => handleBooking(selectedTimeSlot)}>
            Confirm and Book
          </button>
          <button
            onClick={() => handleBooking(selectedTimeSlot, true)}
          >
            Skip Meal
          </button>
          <button type="button" onClick={closeForm}>
            Cancel
          </button>
        </ModalComponent>
      )}
    </div>
  );
};

export default Dates;
