import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import '../styles/UserProfileCategory.css';

const UserProfile = () => {
  const { userId } = useParams();
  const [user, setUser] = useState(null);
  const [photos, setPhotos] = useState([]);

  useEffect(() => {
    fetch(`/api/users/${userId}`)
      .then(response => response.json())
      .then(data => setUser(data))
      .catch(error => console.error('Error fetching user profile:', error));

    fetch(`/api/categories/users/${userId}/photos`)
      .then(response => response.json())
      .then(data => setPhotos(data))
      .catch(error => console.error('Error fetching user photos:', error));
  }, [userId]);

  if (!user) {
    return <p>Loading user profile...</p>;
  }

  return (
    <div className="user-profile">
      {user.profile_photo ? (
        <img src={user.profile_photo} alt={`${user.businessname} profile`} />
      ) : (
        <div className="profile-photo-placeholder">No Profile Photo</div>
      )}
      <h1>{user.businessname}</h1>
      <p>{user.address}</p>
      <p>{user.email}</p>
      <p>{user.phone}</p>
      {/* Add more user details as needed */}

      <div className="user-photos">
        <h2>User Photos</h2>
        {photos.length > 0 ? (
          photos.map((photo, index) => (
            <img key={index} src={photo.photoUrl} alt={`User photo ${index + 1}`} />
          ))
        ) : (
          <p>No photos available for this user</p>
        )}
      </div>
    </div>
  );
};

export default UserProfile;
