// src/components/BusinessUserRegister.js
import React, { useState } from 'react';
import { API_URL } from '../';

const BusinessUserRegister = () => {
  const [businessName, setBusinessName] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(`${API_URL}/api/register/business`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ businessName, username, password }),
      });
      const data = await response.json();
      alert(data.message);
    } catch (error) {
      console.error('Error registering business user:', error);
      alert('Error registering business user. Please try again.');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Business Name:
        <input type="text" value={businessName} onChange={(e) => setBusinessName(e.target.value)} />
      </label>
      <label>
        Username:
        <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} />
      </label>
      <label>
        Password:
        <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
      </label>
      <button type="submit">Register</button>
    </form>
  );
};

export default BusinessUserRegister;
