import React, { useState, useEffect } from 'react';
import '../styles/Services.css';

const Services = () => {
  const [exclusiveMeal, setExclusiveMeal] = useState(null); // Single exclusive meal
  const [regularMeals, setRegularMeals] = useState([]);
  const [newMeal, setNewMeal] = useState({ name: '', description: '', image: null, price: '' });
  const [showExclusiveForm, setShowExclusiveForm] = useState(false);
  const [showRegularForm, setShowRegularForm] = useState(false);
  const [showPhotoUpdateForm, setShowPhotoUpdateForm] = useState(false);
  const [editMode, setEditMode] = useState({ isEditing: false, mealType: '', mealId: null });

  useEffect(() => {
    // Fetch regular meals from the backend
    fetch('/api/meals/regular')
      .then(response => response.json())
      .then(data => setRegularMeals(data))
      .catch(error => console.error('Error fetching regular meals:', error));

    // Fetch exclusive meal from the backend
    fetch('/api/meals/exclusive')
      .then(response => response.json())
      .then(data => setExclusiveMeal(data[0])) // Assuming you want the first exclusive meal
      .catch(error => console.error('Error fetching exclusive meal:', error));
  }, []);

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();

    reader.onloadend = () => {
      setNewMeal((prevMeal) => ({
        ...prevMeal,
        image: reader.result
      }));
    };

    if (file) {
      reader.readAsDataURL(file);
    }
  };

  const addExclusiveMeal = () => {
    if (newMeal.name && newMeal.description && newMeal.image && newMeal.price) {
      const formData = new FormData();
      formData.append('name', newMeal.name);
      formData.append('description', newMeal.description);
      formData.append('price', newMeal.price);
      formData.append('image', newMeal.image);

      fetch('/api/meals/exclusive', {
        method: 'POST',
        body: formData,
      })
      .then(response => response.json())
      .then(data => {
        console.log('Exclusive meal added:', data);
        setExclusiveMeal(newMeal);
        setNewMeal({ name: '', description: '', image: null, price: '' });
        setShowExclusiveForm(false);
      })
      .catch(error => console.error('Error adding exclusive meal:', error));
    } else {
      alert("Please fill in all fields for the exclusive meal.");
    }
  };

  const addRegularMeal = () => {
    if (newMeal.name && newMeal.description && newMeal.image && newMeal.price) {
      const formData = new FormData();
      formData.append('name', newMeal.name);
      formData.append('description', newMeal.description);
      formData.append('price', newMeal.price);
      formData.append('image', newMeal.image);

      fetch('/api/meals/regular', {
        method: 'POST',
        body: formData,
      })
      .then(response => response.json())
      .then(data => {
        console.log('Regular meal added:', data);
        setRegularMeals([...regularMeals, newMeal]);
        setNewMeal({ name: '', description: '', image: null, price: '' });
        setShowRegularForm(false);
      })
      .catch(error => console.error('Error adding regular meal:', error));
    } else {
      alert("Please fill in all fields for the regular meal.");
    }
  };

  const updateMealDetails = () => {
    if (newMeal.name && newMeal.description && newMeal.price) {
      const body = {
        name: newMeal.name,
        description: newMeal.description,
        price: newMeal.price,
      };

      fetch(`/api/meals/${editMode.mealType}/${editMode.mealId}/details`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(body),
      })
      .then(response => response.json())
      .then(data => {
        console.log(`${editMode.mealType} meal details updated:`, data);
        setNewMeal({ name: '', description: '', image: null, price: '' });
        setShowExclusiveForm(false);
        setShowRegularForm(false);
        setEditMode({ isEditing: false, mealType: '', mealId: null });
        if (editMode.mealType === 'exclusive') {
          setExclusiveMeal((prevMeal) => ({ ...prevMeal, ...body }));
        } else {
          setRegularMeals((prevMeals) =>
            prevMeals.map((meal) =>
              meal.id === editMode.mealId ? { ...meal, ...body } : meal
            )
          );
        }
      })
      .catch(error => console.error('Error updating meal details:', error));
    } else {
      alert("Please fill in all fields for the meal details.");
    }
  };

  const updateMealPhoto = () => {
    if (newMeal.image) {
      const formData = new FormData();
      formData.append('image', newMeal.image);

      fetch(`/api/meals/${editMode.mealType}/${editMode.mealId}/photo`, {
        method: 'PUT',
        body: formData,
      })
      .then(response => response.json())
      .then(data => {
        console.log(`${editMode.mealType} meal photo updated:`, data);
        setNewMeal({ name: '', description: '', image: null, price: '' });
        setShowPhotoUpdateForm(false);
        setEditMode({ isEditing: false, mealType: '', mealId: null });
        if (editMode.mealType === 'exclusive') {
          setExclusiveMeal((prevMeal) => ({ ...prevMeal, image: newMeal.image }));
        } else {
          setRegularMeals((prevMeals) =>
            prevMeals.map((meal) =>
              meal.id === editMode.mealId ? { ...meal, image: newMeal.image } : meal
            )
          );
        }
      })
      .catch(error => console.error('Error updating meal photo:', error));
    } else {
      alert("Please select an image to upload.");
    }
  };

  const startEditMeal = (mealType, meal) => {
    setEditMode({ isEditing: true, mealType, mealId: meal.id });
    setNewMeal({
      name: meal.name,
      description: meal.description,
      price: meal.price,
      image: meal.image,
    });
    if (mealType === 'exclusive') {
      setShowExclusiveForm(true);
    } else {
      setShowRegularForm(true);
    }
  };

  const startPhotoUpdate = (mealType, mealId) => {
    setEditMode({ isEditing: true, mealType, mealId });
    setShowPhotoUpdateForm(true);
  };

  const deleteMeal = (mealType, mealId) => {
    fetch(`/api/meals/${mealType}/${mealId}`, {
      method: 'DELETE',
    })
    .then(response => response.json())
    .then(data => {
      console.log(`${mealType} meal deleted:`, data);
      if (mealType === 'exclusive') {
        setExclusiveMeal(null);
      } else {
        setRegularMeals(regularMeals.filter(meal => meal.id !== mealId));
      }
    })
    .catch(error => console.error('Error deleting meal:', error));
  };

  return (
    <div className="profile-container">
      <h1>Hall Services</h1>

      <h2>Exclusive Meal</h2>
      {!showExclusiveForm && !showPhotoUpdateForm && (
        <button onClick={() => setShowExclusiveForm(true)}>Add Exclusive Meal</button>
      )}
      {showExclusiveForm && (
  <div>
    <input
      type="text"
      placeholder="Meal Name"
      value={newMeal.name || ''}
      onChange={(e) => setNewMeal({ ...newMeal, name: e.target.value })}
    />
    <input
      type="text"
      placeholder="Description"
      value={newMeal.description || ''}
      onChange={(e) => setNewMeal({ ...newMeal, description: e.target.value })}
    />
    <input
      type="number"
      placeholder="Price"
      value={newMeal.price || ''}
      onChange={(e) => setNewMeal({ ...newMeal, price: e.target.value })}
    />
    <input
      type="file"
      onChange={handleImageUpload} // Handle the file upload
    />
    <button onClick={addExclusiveMeal}>Save Exclusive Meal</button>
    <button onClick={() => setShowExclusiveForm(false)}>Cancel</button>
  </div>
)}
      {showPhotoUpdateForm && (
        <div>
          <input type="file" onChange={handleImageUpload} />
          <button onClick={updateMealPhoto}>Save Photo</button>
          <button onClick={() => setShowPhotoUpdateForm(false)}>Cancel</button>
        </div>
      )}
      {exclusiveMeal && !showExclusiveForm && !showPhotoUpdateForm && (
        <div className="meal-container">
          <img src={`/uploads/${exclusiveMeal.image}`} alt={exclusiveMeal.name} style={{ width: '100px', height: '100px' }} />
          <h3>{exclusiveMeal.name}</h3>
          <p>{exclusiveMeal.description}</p>
          <p>{exclusiveMeal.price}</p>
          <div className="dropdown">
            <button className="dropbtn">⋮</button>
            <div className="dropdown-content">
              <button onClick={() => startEditMeal('exclusive', exclusiveMeal)}>Update Details</button>
              <button onClick={() => startPhotoUpdate('exclusive', exclusiveMeal.id)}>Update Photo</button>
              <button onClick={() => deleteMeal('exclusive', exclusiveMeal.id)}>Remove</button>
            </div>
          </div>
        </div>
      )}

      <h2>Regular Meals</h2>
      {!showRegularForm && !showPhotoUpdateForm && (
        <button onClick={() => setShowRegularForm(true)}>Add Regular Meal</button>
      )}
      {showRegularForm && (
        <div>
          <input
            type="text"
            placeholder="Meal Name"
            value={newMeal.name || ''}
            onChange={(e) => setNewMeal({ ...newMeal, name: e.target.value })}
          />
          <input
            type="text"
            placeholder="Description"
            value={newMeal.description || ''}
            onChange={(e) => setNewMeal({ ...newMeal, description: e.target.value })}
          />
          <input
            type="number"
            placeholder="Price"
            value={newMeal.price || ''}
            onChange={(e) => setNewMeal({ ...newMeal, price: e.target.value })}
          />
          <button onClick={updateMealDetails}>Save Details</button>
          <button onClick={() => setShowRegularForm(false)}>Cancel</button>
        </div>
      )}
      {showPhotoUpdateForm && (
        <div>
          <input type="file" onChange={handleImageUpload} />
          <button onClick={updateMealPhoto}>Save Photo</button>
          <button onClick={() => setShowPhotoUpdateForm(false)}>Cancel</button>
        </div>
      )}
      <ul>
        {regularMeals.map((meal, index) => (
          <li key={index} className="meal-container">
            <img src={`/uploads/${meal.image}`} alt={meal.name} style={{ width: '100px', height: '100px' }} />
            <h3>{meal.name}</h3>
            <p>{meal.description}</p>
            <p>{meal.price}</p>
            <div className="dropdown">
              <button className="dropbtn">⋮</button>
              <div className="dropdown-content">
                <button onClick={() => startEditMeal('regular', meal)}>Update Details</button>
                <button onClick={() => startPhotoUpdate('regular', meal.id)}>Update Photo</button>
                <button onClick={() => deleteMeal('regular', meal.id)}>Remove</button>
              </div>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Services;
