import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import NavbarBooking from './NavbarBooking';
import CalendarComponent from './Dates/CalendarComponent';
import ModalComponent from './Dates/ModalComponent';
import MealSelection from './Dates/MealSelection';
import TimeSlotSelection from './Dates/TimeSlotSelection';
import { handleInputChange } from './Dates/handleInputChange';
import { handleMealSelection } from './Dates/handleMealSelection';
import { handleTimeSelection } from './Dates/handleTimeSelection';
import { handleBookingSubmission } from './Dates/handleBookingSubmission';
import { handleSelectSlot } from './Dates/handleSelectSlot';
import '../styles/Dates.css';

const Dates = () => {
    const { userId } = useParams();
    const [category, setCategory] = useState("");
    const [events, setEvents] = useState([]);
    const [loading, setLoading] = useState(true);
    const [selectedDate, setSelectedDate] = useState(null);
    const [selectedTimeSlot, setSelectedTimeSlot] = useState(null);
    const [showModal, setShowModal] = useState(false);
    const [showMealsSelection, setShowMealsSelection] = useState(false);
    const [bookingStatus, setBookingStatus] = useState([]);
    const [userType, setUserType] = useState(null);
    const [numOfPeople, setNumOfPeople] = useState("");
    const [hallCapacity, setHallCapacity] = useState(null);
    const [minGuests, setMinGuests] = useState(null);
    const [exclusiveMeal, setExclusiveMeal] = useState(null);
    const [regularMeals, setRegularMeals] = useState([]);
    const [totalPrice, setTotalPrice] = useState(0);
    const [pricePerEvent, setPricePerEvent] = useState(0);
    const [advancePayment, setAdvancePayment] = useState(0);
    const [selectedMeal, setSelectedMeal] = useState(null);
    const [formData, setFormData] = useState({
        firstName: "",
        lastName: "",
        idNum: "",
        phoneNumber: "",
        email: "",
        num_of_people: 0,
        totalPrice: 0,
        priceRemaining: 0,
        paid: 0,
    });

    useEffect(() => {
        // Fetching logic here...
    }, [userId]);

    const closeForm = () => {
        setSelectedMeal(null);
        setTotalPrice(pricePerEvent);
        setSelectedDate(null);
        setSelectedTimeSlot(null);
        setNumOfPeople(0);
        setFormData({
            firstName: "",
            lastName: "",
            idNum: "",
            phoneNumber: "",
            email: "",
            num_of_people: 0,
            totalPrice: 0,
            priceRemaining: 0,
            paid: 0,
        });
        setShowModal(false);
        setShowMealsSelection(false);
    };
    const calculateTotalPrice = (mealPrice = 0) => {
        const total =
          category === "Hall"
            ? mealPrice * numOfPeople + pricePerEvent
            : pricePerEvent;
        setTotalPrice(total);
        setAdvancePayment((total * 0.05).toFixed(2)); // Calculate 5% advance payment
      };

    return (
        <div className='dates'>
            <NavbarBooking userId={userId} />
            <h2>Booked Dates</h2>
            <CalendarComponent
                events={events}
                onSelectSlot={(slot) =>
                    handleSelectSlot(slot, setSelectedDate, setBookingStatus, setShowModal, userType)
                }
            />
            {showModal && (
                <ModalComponent onClose={closeForm}>
                    <TimeSlotSelection
                        category={category}
                        numOfPeople={numOfPeople}
                        minGuests={minGuests}
                        hallCapacity={hallCapacity}
                        selectedDate={selectedDate}
                        pricePerEvent={pricePerEvent}
                        dayBooking={bookingStatus.some(booking => booking.time_slot === "day")}
                        nightBooking={bookingStatus.some(booking => booking.time_slot === "night")}
                        onTimeSelection={(time) =>
                            handleTimeSelection(time, category, numOfPeople, minGuests, hallCapacity, setShowModal, setShowMealsSelection, setSelectedTimeSlot)
                        }
                        onInputChange={(e) => handleInputChange(e, setNumOfPeople)}
                    />
                </ModalComponent>
            )}
            <ModalComponent isOpen={showMealsSelection} onClose={() => setShowMealsSelection(false)}>
                <MealSelection
                    exclusiveMeal={exclusiveMeal}
                    regularMeals={regularMeals}
                    selectedMeal={selectedMeal}
                    onMealSelection={(meal) =>
                        handleMealSelection(meal, selectedMeal, setSelectedMeal, calculateTotalPrice, setTotalPrice, setAdvancePayment, pricePerEvent)
                    }
                    totalPrice={totalPrice}
                    advancePayment={advancePayment}
                    onBooking={() => handleBookingSubmission(selectedTimeSlot, category, numOfPeople, selectedMeal, userId, selectedDate, totalPrice, advancePayment, closeForm, pricePerEvent)}
                    onSkipMeal={() => handleBookingSubmission(selectedTimeSlot, category, numOfPeople, null, userId, selectedDate, totalPrice, advancePayment, closeForm, pricePerEvent)}
                />
            </ModalComponent>
        </div>
    );
};

export default Dates;
