import React from "react";

const PhotoGallerySection = ({ photos, handlePhotoChange, handlePhotoUpload }) => (
  <div>
    <h2>Photos</h2>
    <input 
      type='file' 
      multiple 
      onChange={(e) => {
        handlePhotoChange(e);
        handlePhotoUpload(e); // Automatically upload when a file is selected
      }} 
    />
    <div className='photo-gallery'>
      {photos.map((photo, index) => (
        <div key={index}>
          <img
            src={photo}
            alt={`Photo ${index + 1}`}
            className='business-photo'
          />
          <p>Photo {index + 1}</p>
        </div>
      ))}
    </div>
  </div>
);

export default PhotoGallerySection;
