import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/UserProfile.css';

const UserProfile = () => {
  const [user, setUser] = useState(null);
  const [photos, setPhotos] = useState([]);
  const [loading, setLoading] = useState(true); // Add loading state
  const [error, setError] = useState(null); // Add error state
  const userId = localStorage.getItem('userId');
  const navigate = useNavigate();

  useEffect(() => {
    if (!userId) {
      navigate('/login'); // Redirect to login if no userId is found
      return;
    }

    const fetchUser = async () => {
      try {
        const response = await fetch(`/api/users/${userId}`);
        if (!response.ok) {
          throw new Error(`Failed to fetch user: ${response.statusText}`);
        }
        const data = await response.json();
        console.log('Fetched user:', data); // Debugging log
        setUser(data);
      } catch (err) {
        console.error('Error fetching user profile:', err);
        setError(err.message);
      }
    };

    const fetchPhotos = async () => {
      try {
        const response = await fetch(`/api/categories/users/${userId}/photos`);
        if (!response.ok) {
          throw new Error(`Failed to fetch photos: ${response.statusText}`);
        }
        const data = await response.json();
        console.log('Fetched photos:', data); // Debugging log
        setPhotos(data);
      } catch (err) {
        console.error('Error fetching user photos:', err);
        setError(err.message);
      }
    };

    fetchUser();
    fetchPhotos();
    setLoading(false); // Ensure loading state is updated after fetch
  }, [userId, navigate]);

  if (loading) {
    return <p>Loading user profile...</p>;
  }

  if (error) {
    return <p>Error loading user profile: {error}</p>;
  }

  if (!user) {
    return <p>User not found</p>;
  }

  return (
    <div className="user-profile">
      {user.profile_photo ? (
        <img src={user.profile_photo} alt={`${user.businessname} profile`} />
      ) : (
        <div className="profile-photo-placeholder">No Profile Photo</div>
      )}
      <h1>{user.businessname}</h1>
      <p>{user.address}</p>
      <p>{user.email}</p>
      <p>{user.phone}</p>
      {/* Add more user details as needed */}

      <div className="user-photos">
        <h2>User Photos</h2>
        {photos.length > 0 ? (
          photos.map((photo, index) => (
            <img key={index} src={photo.photoUrl} alt={`User photo ${index + 1}`} />
          ))
        ) : (
          <p>No photos available for this user</p>
        )}
      </div>
    </div>
  );
};

export default UserProfile;
