import React from "react";
import MealOption from "./MealOption";
import Modal from "./Modal";

const MealSelectionModal = ({
  showMealsSelection,
  regularMeals,
  exclusiveMeal,
  selectedMeal,
  handleMealSelection,
  totalPrice,
  advancePayment,
  handleBooking,
  closeForm,
}) => {
  return (
    <Modal isOpen={showMealsSelection} onClose={closeForm}>
      <h3>Select a Meal for Your Event</h3>
      <div className="meals-container">
        <h4>Exclusive Meals</h4>
        <ul>
          {exclusiveMeal && (
            <MealOption
              meal={exclusiveMeal}
              selectedMeal={selectedMeal}
              handleMealSelection={handleMealSelection}
            />
          )}
        </ul>

        <h4>Regular Meals</h4>
        <ul>
          {regularMeals.map((meal) => (
            <MealOption
              key={meal.id}
              meal={meal}
              selectedMeal={selectedMeal}
              handleMealSelection={handleMealSelection}
            />
          ))}
        </ul>
      </div>
      <h4>Total Price: ${totalPrice}</h4>
      <h4>Advance Payment (5%): ${advancePayment}</h4>
      <button onClick={() => handleBooking()}>Confirm and Book</button>
      <button type="button" onClick={closeForm}>
        Cancel
      </button>
    </Modal>
  );
};

export default MealSelectionModal;
