// src/components/BookingCalendar.js
import {API_URL} from '../constans';

import React, { useState, useEffect } from 'react';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import 'react-date-picker/dist/DatePicker.css';

const localizer = momentLocalizer(moment);


const BookingCalendar = () => {
  const [selectedDate, setSelectedDate] = useState(null);
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    idNum: '',
    phoneNumber: '',
  });

  const handleDateClick = (info) => {
    const date = info.date;
    const startOfDay = moment(date).startOf('day');
    const endOfDay = moment(date).endOf('day');

    setSelectedDate({
      start: startOfDay.toDate(),
      end: endOfDay.toDate(),
    });
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({ ...prevData, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const numOfBusiness = localStorage.getItem('numOfBusiness'); // Assuming you store this in localStorage

    fetch(`${API_URL}/api/date-selections`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ ...formData, date: selectedDate, numOfBusiness }),
    })
      .then((response) => response.json())
      .then((data) => {
        alert(data.message);
        // Optionally, reset form and selected date
        setFormData({ firstName: '', lastName: '', idNum: '', phoneNumber: '' });
        setSelectedDate(null);
      })
      .catch((error) => console.error('Error storing date selection:', error));
  };

  return (
    <div className="fullcalendar-container">
      <FullCalendar
        plugins={[dayGridPlugin, timeGridPlugin]}
        initialView="dayGridMonth"
        dateClick={handleDateClick}
      />
      {selectedDate && (
        <form className="date-form" onSubmit={handleSubmit}>
          <h3>Selected Date: {moment(selectedDate.start).format('MMMM Do YYYY')}</h3>
          <div className="form-group">
            <label>First Name</label>
            <input
              type="text"
              name="firstName"
              value={formData.firstName}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Last Name</label>
            <input
              type="text"
              name="lastName"
              value={formData.lastName}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>ID Number</label>
            <input
              type="text"
              name="idNum"
              value={formData.idNum}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Phone Number</label>
            <input
              type="text"
              name="phoneNumber"
              value={formData.phoneNumber}
              onChange={handleInputChange}
              required
            />
          </div>
          <button type="submit">Submit</button>
        </form>
      )}
    </div>
  );
};

export default BookingCalendar;
