// src/components/BookingCalendar.js
import React, { useState, useEffect } from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import { API_URL } from '../constants';
import '../styles/FullCalendarComponent.css';

const BookingCalendar = () => {
  const [events, setEvents] = useState([]);
  const [selectedDate, setSelectedDate] = useState(null);
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    idNum: '',
    phoneNumber: '',
  });

  useEffect(() => {
    // Fetch events from the API
    fetch(`${API_URL}/api/bookings`)
      .then((response) => response.json())
      .then((data) => {
        const formattedEvents = data.map((event) => ({
          title: `${event.firstName} ${event.lastName} (${event.time_slot})`,
          start: event.date,
          end: event.date,
        }));
        setEvents(formattedEvents);
      })
      .catch((error) => console.error('Error fetching events:', error));
  }, []);

  const handleDateClick = (date) => {
    setSelectedDate(date);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({ ...prevData, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const numOfBusiness = localStorage.getItem('numOfBusiness'); // Assuming you store this in localStorage

    fetch(`${API_URL}/api/bookings`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ ...formData, date: selectedDate, numOfBusiness }),
    })
      .then((response) => response.json())
      .then((data) => {
        alert(data.message);
        // Optionally, reset form and selected date
        setFormData({ firstName: '', lastName: '', idNum: '', phoneNumber: '' });
        setSelectedDate(null);
        // Update events
        setEvents((prevEvents) => [...prevEvents, {
          title: `${formData.firstName} ${formData.lastName} (${formData.time_slot})`,
          start: selectedDate,
          end: selectedDate,
        }]);
      })
      .catch((error) => console.error('Error storing date selection:', error));
  };

  return (
    <div className="booking-calendar-container">
      <FullCalendar
        plugins={[dayGridPlugin]}
        initialView="dayGridMonth"
        events={events}
        dateClick={(arg) => handleDateClick(arg.dateStr)}
      />
      {selectedDate && (
        <form className="date-form" onSubmit={handleSubmit}>
          <h3>Selected Date: {selectedDate}</h3>
          <div className="form-group">
            <label>First Name</label>
            <input
              type="text"
              name="firstName"
              value={formData.firstName}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Last Name</label>
            <input
              type="text"
              name="lastName"
              value={formData.lastName}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>ID Number</label>
            <input
              type="text"
              name="idNum"
              value={formData.idNum}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Phone Number</label>
            <input
              type="text"
              name="phoneNumber"
              value={formData.phoneNumber}
              onChange={handleInputChange}
              required
            />
          </div>
          <button type="submit">Submit</button>
        </form>
      )}
    </div>
  );
};

export default BookingCalendar;
