import React, { useState } from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import '../styles/FullCalendarComponent.css';

const FullCalendarComponent = () => {
  const [selectedDate, setSelectedDate] = useState(null);
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    idNum: '',
    phoneNumber: '',
  });

  const handleDateClick = (arg) => {
    setSelectedDate(arg.dateStr);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({ ...prevData, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert(`Date: ${selectedDate}, First Name: ${formData.firstName}, Last Name: ${formData.lastName}, ID: ${formData.idNum}, Phone: ${formData.phoneNumber}`);
    // Reset form and selected date
    setFormData({ firstName: '', lastName: '', idNum: '', phoneNumber: '' });
    setSelectedDate(null);
  };

  return (
    <div className="fullcalendar-container">

  (
        <form className="date-form" onSubmit={handleSubmit}>
          <h3>Selected Date: {selectedDate}</h3>
          <div className="form-group">
            <label>First Name</label>
            <input
              type="text"
              name="firstName"
              value={formData.firstName}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Last Name</label>
            <input
              type="text"
              name="lastName"
              value={formData.lastName}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>ID Number</label>
            <input
              type="text"
              name="idNum"
              value={formData.idNum}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Phone Number</label>
            <input
              type="text"
              name="phoneNumber"
              value={formData.phoneNumber}
              onChange={handleInputChange}
              required
            />
          </div>
          <button type="submit">Submit</button>
        </form>
      )}
    </div>
  );
};

export default FullCalendarComponent;
