// src/components/BookingCalendar.js
import { API_URL } from '../constans';
import React, { useState, useEffect } from 'react';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import '../styles/BookingCalendar.css'; // Import your custom CSS

const localizer = momentLocalizer(moment);

const BookingCalendar = () => {
  const [events, setEvents] = useState([]);
  const [selectedDate, setSelectedDate] = useState(null);
  const [selectedTime, setSelectedTime] = useState(null);
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    idNum: '',
    phoneNumber: '',
  });
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    fetch(`${API_URL}/api/events`)
      .then((response) => response.json())
      .then((data) => setEvents(data))
      .catch((error) => console.error('Error fetching events:', error));
  }, []);

  const handleSelectSlot = ({ start }) => {
    setSelectedDate(start);
    setShowModal(true);
  };

  const handleTimeSelection = (time) => {
    setSelectedTime(time);
    setShowModal(false);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({ ...prevData, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const numOfBusiness = localStorage.getItem('numOfBusiness'); // Assuming you store this in localStorage

    fetch(`${API_URL}/api/events`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        title: `${formData.firstName} ${formData.lastName} (${selectedTime})`,
        start: selectedDate,
        end: selectedDate,
        description: formData.idNum,
        numOfBusiness,
        timeSlot: selectedTime // Add the selected time slot to the event
      }),
    })
      .then((response) => response.json())
      .then((data) => {
        alert(data.message);
        // Optionally, reset form and selected date
        setFormData({ firstName: '', lastName: '', idNum: '', phoneNumber: '' });
        setSelectedDate(null);
        setSelectedTime(null);
        // Update events
        setEvents((prevEvents) => [
          ...prevEvents,
          {
            start: selectedDate,
            end: selectedDate,
            title: `${formData.firstName} ${formData.lastName} (${selectedTime})`,
          },
        ]);
      })
      .catch((error) => console.error('Error storing date selection:', error));
  };

  const isDateBooked = (date, time) => {
    return events.some(
      (event) =>
        moment(event.start).isSame(moment(date), 'day') &&
        event.title.includes(time)
    );
  };

  const eventPropGetter = (event) => {
    const backgroundColor = isDateBooked(event.start, 'day') && isDateBooked(event.start, 'night')
      ? '#ff0000'
      : '#3174ad';
    return { style: { backgroundColor } };
  };

  return (
    <div className="booking-calendar-container">
      <Calendar
        localizer={localizer}
        events={events}
        selectable
        onSelectSlot={handleSelectSlot}
        defaultView="month"
        style={{ height: 500 }}
        eventPropGetter={eventPropGetter}
        views={{ month: true, agenda: true }}
      />
      {selectedDate && (
        <form className="date-form" onSubmit={handleSubmit}>
          <h3>Selected Date: {moment(selectedDate).format('MMMM Do YYYY')}</h3>
          <div className="form-group">
            <label>First Name</label>
            <input
              type="text"
              name="firstName"
              value={formData.firstName}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Last Name</label>
            <input
              type="text"
              name="lastName"
              value={formData.lastName}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>ID Number</label>
            <input
              type="text"
              name="idNum"
              value={formData.idNum}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Phone Number</label>
            <input
              type="text"
              name="phoneNumber"
              value={formData.phoneNumber}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Time Slot</label>
            <select
              name="timeSlot"
              value={selectedTime}
              onChange={(e) => setSelectedTime(e.target.value)}
              required
            >
              <option value="" disabled>Select Time Slot</option>
              <option value="day">Day</option>
              <option value="night">Night</option>
            </select>
          </div>
          <button type="submit">Submit</button>
        </form>
      )}
      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <h3>Select Time Slot for {moment(selectedDate).format('MMMM Do YYYY')}</h3>
            <button onClick={() => handleTimeSelection('day')}>Day</button>
            <button onClick={() => handleTimeSelection('night')}>Night</button>
            <button onClick={() => setShowModal(false)}>Cancel</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default BookingCalendar;
