export const calculateTotalPrice = ({
    mealPrice = 0,
    category,
    numOfPeople,
    pricePerEvent,
    setTotalPrice,
    setAdvancePayment,
  }) => {
    const total =
      category === "Hall"
        ? mealPrice * numOfPeople + pricePerEvent
        : pricePerEvent;
    setTotalPrice(total);
    setAdvancePayment((total * 0.05).toFixed(2));
  };
