import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { API_URL } from '../constans';
import '../styles/CategoryUsers.css';

const CategoryUsers = () => {
  const { categoryId } = useParams();
  const [users, setUsers] = useState([]);

  useEffect(() => {
    fetch(`${API_URL}/api/users/category/${categoryId}`)
      .then(response => response.json())
      .then(data => {
        console.log('Fetched users:', data); // Debugging statement
        setUsers(data);
      })
      .catch(error => console.error('Error fetching users:', error));
  }, [categoryId]);

  return (
    <div className="category-users-container">
      <h1>Users in Category</h1>
      <div className="users-grid">
        {users.length > 0 ? (
          users.map(user => (
            <div key={user.id} className="user-item">
              <p><strong>{user.businessname}</strong></p>
              <p>{user.address}</p>
              <p>{user.email}</p>
            </div>
          ))
        ) : (
          <p>No users available</p>
        )}
      </div>
    </div>
  );
};

export default CategoryUsers;
