import React, { useContext } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Login from './pages/Login';
import Register from './components/RegularUserRegister';
import BusinessUserRegister from './components/BusinessUserRegister';
import Profile from './pages/Profile';
import BusinessProfile from './pages/BusinessProfile';
import Financial from './pages/Financial';
import Dates from './pages/Dates';
import Services from './pages/Services';
import AuthContext from './contexts/AuthContext';
import Layout from './components/Layout';
import Home from './pages/Home';
import RegistrationSelection from './pages/Register';
import BusinessNavbar from './components/BusinessNavbar';

const App = () => {
  const { isAuthenticated } = useContext(AuthContext);
  const userType = localStorage.getItem('userType'); // Get userType from localStorage

  return (
    <Router>
      <Routes>
        <Route element={<Layout />}>
          <Route path="/" element={<Home />} />
          <Route path="/login" element={isAuthenticated ? <Navigate to="/profile" /> : <Login />} />
          <Route path="/register" element={isAuthenticated ? <Navigate to="/profile" /> : <Register />} />
          <Route path="/register-business" element={isAuthenticated ? <Navigate to="/profile" /> : <BusinessUserRegister />} />
        </Route>
        <Route element={<Layout />}>
          <Route
            path="/profile"
            element={
              isAuthenticated ? (
                userType === 'business' ? (
                  <>
                    <BusinessNavbar />
                    <BusinessProfile />
                  </>
                ) : (
                  <Profile />
                )
              ) : (
                <Navigate to="/login" />
              )
            }
          />
          <Route path="/financial" element={isAuthenticated ? <><BusinessNavbar /><Financial /></> : <Navigate to="/login" />} />
          <Route path="/dates" element={isAuthenticated ? <><BusinessNavbar /><Dates /></> : <Navigate to="/login" />} />
          <Route path="/services" element={isAuthenticated ? <><BusinessNavbar /><Services /></> : <Navigate to="/login" />} />
        </Route>
      </Routes>
    </Router>
  );
};

export default App;
