import React from "react";
import "../../styles/Dates.css";

const MealSelection = ({ exclusiveMeal, regularMeals, selectedMeal, onSelectMeal }) => {
  return (
    <div className="meal-selection">
      <h4>Exclusive Meals</h4>
      <ul>
        {exclusiveMeal && (
          <li
            key={exclusiveMeal.id}
            className={`meal-option ${selectedMeal?.id === exclusiveMeal.id ? "selected" : ""}`}
            onClick={() => onSelectMeal(exclusiveMeal)}
          >
            <input
              type="radio"
              name="meal"
              value={exclusiveMeal.id}
              checked={selectedMeal?.id === exclusiveMeal.id}
              onChange={() => onSelectMeal(exclusiveMeal)}
            />
            {exclusiveMeal.name} - ${exclusiveMeal.price}
            <img
              src={exclusiveMeal.image}
              alt={exclusiveMeal.name}
              className="meal-photo"
            />
          </li>
        )}
      </ul>
      <h4>Regular Meals</h4>
      <ul>
        {regularMeals.map((meal) => (
          <li
            key={meal.id}
            className={`meal-option ${selectedMeal?.id === meal.id ? "selected" : ""}`}
            onClick={() => onSelectMeal(meal)}
          >
            <input
              type="radio"
              name="meal"
              value={meal.id}
              checked={selectedMeal?.id === meal.id}
              onChange={() => onSelectMeal(meal)}
            />
            {meal.name} - ${meal.price}
            <img
              src={meal.image}
              alt={meal.name}
              className="meal-photo"
            />
          </li>
        ))}
      </ul>
    </div>
  );
};

export default MealSelection;
