import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { API_URL } from '../constans';
import ProfilePhotoSection from '../components/ProfilePhotoSection';
import ProfileDetailsForm from '../components/ProfileDetailsForm';
import ProfileDetailsDisplay from './ProfileDetailsDisplay';
import '../styles/styles.css';

const Profile = () => {
  const [userDetails, setUserDetails] = useState({});
  const [editing, setEditing] = useState(false);
  const [formData, setFormData] = useState({});
  const [profilePhoto, setProfilePhoto] = useState(null);
  const [newProfilePhoto, setNewProfilePhoto] = useState(null);
  const navigate = useNavigate();

  const userId = localStorage.getItem('userId');

  useEffect(() => {
    if (!userId) {
      console.error('No user ID provided');
      return;
    }
    console.log(`Fetching profile for regular user with ID: ${userId}`);
    fetch(`${API_URL}/api/profile/regular/${userId}`)
      .then(response => {
        console.log('Response status:', response.status);
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        console.log('Fetched user details:', data);
        setUserDetails(data);
        setFormData(data);
        setProfilePhoto(data.profilePhoto);
      })
      .catch(error => {
        console.error('Error fetching user details:', error);
        setUserDetails({ error: 'Error fetching user details' });
      });
  }, [userId]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleProfilePhotoChange = (e) => {
    setNewProfilePhoto(e.target.files[0]);
  };

  const handleProfilePhotoUpload = (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('profilePhoto', newProfilePhoto);

    fetch(`${API_URL}/api/profile/regular/${userId}/profile-photo`, {
      method: 'POST',
      body: formData
    })
      .then(response => response.json())
      .then(data => {
        alert(data.message);
        setNewProfilePhoto(null);
        setProfilePhoto(data.profilePhoto);
      })
      .catch(error => console.error('Error uploading profile photo:', error));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    fetch(`${API_URL}/api/profile/regular/${userId}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(formData)
    })
      .then(response => response.json())
      .then(data => {
        alert(data.message);
        setEditing(false);
        setUserDetails(formData);
      })
      .catch(error => console.error('Error updating user details:', error));
  };

  const handleLogout = () => {
    localStorage.removeItem('userId');
    localStorage.removeItem('userType');
    navigate('/login');
    window.location.reload();
  };

  if (!userId) {
    return <p>Error: No user ID provided</p>;
  }

  if (userDetails.error) {
    return <p>{userDetails.error}</p>;
  }

  return (
    <div className="profile-container">
      <h1>Regular User Profile</h1>
      <ProfilePhotoSection
        profilePhoto={profilePhoto}
        handleProfilePhotoChange={handleProfilePhotoChange}
        handleProfilePhotoUpload={handleProfilePhotoUpload}
      />
      {editing ? (
        <ProfileDetailsForm
          formData={formData}
          handleInputChange={handleInputChange}
          handleSubmit={handleSubmit}
        />
      ) : (
        <ProfileDetailsDisplay
          userDetails={userDetails}
          setEditing={setEditing}
        />
      )}
      <button onClick={handleLogout} className="logout-button">Logout</button>
    </div>
  );
};

export default Profile;
