import React from "react";

const BusinessProfileDetailsForm = ({
  formData,
  handleInputChange,
  handleSubmit,
  category // Assuming category is passed as a prop
}) => {
  const handlePriceChange = (e) => {
    const value = e.target.value;
    // Only update if the value is greater than 0 or empty
    if (value === "" || parseFloat(value) > 0) {
      handleInputChange(e); // Call the original input change handler
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div className='form-group'>
        <label>Business Name:</label>
        <input
          type='text'
          name='businessName'
          value={formData.businessName || ""}
          onChange={handleInputChange}
        />
      </div>
      <div className='form-group'>
        <label>Address:</label>
        <input
          type='text'
          name='address'
          value={formData.address || ""}
          onChange={handleInputChange}
        />
      </div>
      <div className='form-group'>
        <label>Email:</label>
        <input
          type='email'
          name='email'
          value={formData.email || ""}
          onChange={handleInputChange}
        />
      </div>
      {/* Conditionally render hall capacity if category is "Hall" */}
      {category === "Hall" && (
        <div className='form-group'>
          <label>Hall Capacity:</label>
          <input
            type='text'
            name='hallCapacity'
            value={formData.hallCapacity || ""}
            onChange={handleInputChange}
          />
        </div>
      )}
      {/* Price per Event field with validation */}
      <div className='form-group'>
        <label>Price per Event:</label>
        <input
          type='number'
          name='pricePerEven"}t'
          value={formData.price_PerEvent || ""}
          onChange={handlePriceChange} // Use the new handler
          min="0" // Prevent negative values in the input
        />
        {/* Optional: Display an error message if the value is invalid */}
        {formData.pricePerEvent <= 0 && (
          <p style={{ color: 'red' }}>Price must be greater than 0.</p>
        )}
      </div>
      <button type='submit'>Save</button>
    </form>
  );
};

export default BusinessProfileDetailsForm;
